let currentTranslations = {};
let currentLanguage = 'PL';

const availableLanguages = {
    'PL': {
        name: 'Polski',
        flag: '🇵🇱',
        file: 'https://raw.githubusercontent.com/BarryV94/Magazyn-Produkt-w/main/lang/PL.json'
    },
    'EN': {
        name: 'English', 
        flag: '🇬🇧',
        file: 'https://raw.githubusercontent.com/BarryV94/Magazyn-Produkt-w/main/lang/EN.json'
    },
    'DE': {
        name: 'Deutsch',
        flag: '🇩🇪', 
        file: 'https://raw.githubusercontent.com/BarryV94/Magazyn-Produkt-w/main/lang/DE.json'
    }
};

// Funkcja ładowania tłumaczenia
async function loadLanguage(langCode) {
    if (!availableLanguages[langCode]) {
        console.error('Language not supported:', langCode);
        return;
    }

    try {
        showLoader('Ładowanie tłumaczenia...');
        const response = await fetch(availableLanguages[langCode].file);

        if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`);
        }

        currentTranslations = await response.json();
        currentLanguage = langCode;

        applyTranslations();
        updateLanguageButton();
        saveLanguagePreference();
        hideLoader();

        showNotification(`Język zmieniony na ${availableLanguages[langCode].name}`, 'success');

    } catch (error) {
        console.error('Error loading language file:', error);
        hideLoader();
        showNotification('Błąd ładowania tłumaczenia', 'danger');
        // Fallback do domyślnego języka
        if (langCode !== 'PL') {
            loadLanguage('PL');
        }
    }
}

// Funkcja aplikująca tłumaczenia
function applyTranslations() {
    // Tłumaczenie elementów z atrybutem data-translate
    document.querySelectorAll('[data-translate]').forEach(element => {
        const key = element.getAttribute('data-translate');
        if (currentTranslations[key]) {
            element.textContent = currentTranslations[key];
        }
    });

    // Tłumaczenie placeholderów
    document.querySelectorAll('[data-translate-placeholder]').forEach(element => {
        const key = element.getAttribute('data-translate-placeholder');
        if (currentTranslations[key]) {
            element.setAttribute('placeholder', currentTranslations[key]);
        }
    });

    // Tłumaczenie tytułów
    document.querySelectorAll('[data-translate-title]').forEach(element => {
        const key = element.getAttribute('data-translate-title');
        if (currentTranslations[key]) {
            element.setAttribute('title', currentTranslations[key]);
        }
    });

    // Tłumaczenie atrybutów alt
    document.querySelectorAll('[data-translate-alt]').forEach(element => {
        const key = element.getAttribute('data-translate-alt');
        if (currentTranslations[key]) {
            element.setAttribute('alt', currentTranslations[key]);
        }
    });

    // Tłumaczenie wartości przycisków
    document.querySelectorAll('[data-translate-value]').forEach(element => {
        const key = element.getAttribute('data-translate-value');
        if (currentTranslations[key]) {
            element.value = currentTranslations[key];
        }
    });
}

// Aktualizacja przycisku języka
function updateLanguageButton() {
    const button = document.getElementById('languageToggle');
    const span = document.getElementById('currentLanguage');
    if (button && span) {
        span.textContent = currentLanguage;
    }
}

// Zapisywanie preferencji języka
function saveLanguagePreference() {
    localStorage.setItem('selectedLanguage', currentLanguage);
}

// Ładowanie preferencji języka
function loadLanguagePreference() {
    const saved = localStorage.getItem('selectedLanguage');
    return saved || 'PL';
}

// Modal języka
function openLanguageModal() {
    const modal = document.getElementById('languageModal');
    const list = document.getElementById('languageList');

    if (!modal || !list) return;

    // Wypełnij listę języków
    list.innerHTML = '';
    Object.entries(availableLanguages).forEach(([code, lang]) => {
        const li = document.createElement('li');
        li.innerHTML = `
            <span class="language-flag">${lang.flag}</span>
            <span>${lang.name}</span>
        `;
        li.onclick = () => loadLanguage(code);

        if (code === currentLanguage) {
            li.classList.add('selected');
        }

        list.appendChild(li);
    });

    modal.style.display = 'flex';
    setTimeout(() => modal.classList.add('show'), 10);
}

function closeLanguageModal() {
    const modal = document.getElementById('languageModal');
    if (modal) {
        modal.classList.remove('show');
        setTimeout(() => modal.style.display = 'none', 300);
    }
}

// Inicjalizacja systemu tłumaczeń
async function initLanguageSystem() {
    const lang = loadLanguagePreference();
    await loadLanguage(lang);

    // Event listener dla przycisku języka
    const languageToggle = document.getElementById('languageToggle');
    if (languageToggle) {
        languageToggle.addEventListener('click', openLanguageModal);
    }
}

function exportTextsForTranslation() {
    const texts = {};

    // Selektory elementów, które zawierają dynamiczne treści (ignoruj)
    const dynamicSelectors = [
        '.price-difference',
        '.stat-value',
        '.summary-value',
        '.detail-value',
        '.log-message',
        '.notification',
        '#totalValue',
        '#purchaseValue',
        '#currentValue',
        '#valueDifference',
        '#totalProfit',
        '#totalLoss',
        '#productsWithPrice',
        '#totalProducts',
        '#profitPercentage',
        '#lossPercentage',
        '.gallery-count',
        '.selection-counter',
        '.gallery-pagination-info',
        '.page-info',
        '.memory-stats span',
        '.browser-info',
        '.progress-text',
        '.product h3',
        '.product-content',
        '.old-product-item',
        '.empty-state',
        '.gold-buyback-value',
        '.preview-detail-value',
        '.extended-stat-value',
        '.extended-list-item',
        '.stat-table td'
    ];

    // Funkcja sprawdzająca czy element jest dynamiczny
    function isDynamicElement(element) {
        // Sprawdź czy element lub jego rodzic jest na liście dynamicznych selektorów
        return dynamicSelectors.some(selector => element.matches(selector) || element.closest(selector));
    }

    // Funkcja sprawdzająca czy tekst jest stały (nie dynamiczny)
    function isStaticText(text) {
        const dynamicPatterns = [
            /^\d+[,.]?\d*\s*[zł€$%]$/i, // ceny, procenty
            /^\d+[,.]?\d*$/, // same liczby
            /^\d+\s*\/\s*\d+$/, // format "10 / 20"
            /^[0-9:]+$/, // godziny, daty numeryczne
            /^\d{4}-\d{2}-\d{2}/, // daty
            /^[0-9.,]+\s*[MBKB]$/i, // rozmiary plików
            /^[0-9.]+%$/, // procenty
            /^(0|100)%$/ // typowe wartości procentowe
        ];

        return !dynamicPatterns.some(pattern => pattern.test(text.trim()));
    }

    // Zbierz teksty z elementów
    document.querySelectorAll('body *').forEach(element => {
        // Pomijaj elementy dynamiczne
        if (isDynamicElement(element)) return;

        // Pomijaj elementy z klasami wskazującymi na dynamiczną zawartość
        if (element.classList.contains('dynamic') || 
            element.classList.contains('calculated') ||
            element.id && element.id.includes('Value') ||
            element.id && element.id.includes('Count') ||
            element.id && element.id.includes('Total')) {
            return;
        }

        // Pomijaj elementy wewnątrz produktów (są dynamiczne)
        if (element.closest('.product') && !element.closest('.button-group')) {
            return;
        }

        // Pomijaj elementy wewnątrz statystyk
        if (element.closest('.stats-container') && !element.closest('h2')) {
            return;
        }

        // Tylko elementy bez dzieci lub z prostą strukturą
        if (element.children.length === 0 || 
            (element.children.length === 1 && element.children[0].tagName === 'I')) {

            const text = element.textContent.trim();

            if (text && 
                text.length > 1 && 
                isStaticText(text) &&
                !texts[text] &&
                // Dodatkowe filtry dla polskich tekstów
                (text.includes(' ') || text.length > 3) && // musi być dłuższy niż 3 znaki lub zawierać spacje
                !text.match(/^[A-Z]{2,4}$/) && // pomijaj skróty walut
                !text.match(/^[0-9]+$/) // pomijaj same liczby
            ) {
                texts[text] = text;
            }
        }
    });

    // Zbierz placeholdery (tylko stałe)
    document.querySelectorAll('[placeholder]').forEach(element => {
        const text = element.getAttribute('placeholder');
        if (text && isStaticText(text) && !texts[text]) {
            texts[text] = text;
        }
    });

    // Zbierz tytuły (tylko stałe)
    document.querySelectorAll('[title]').forEach(element => {
        const text = element.getAttribute('title');
        if (text && isStaticText(text) && !texts[text]) {
            texts[text] = text;
        }
    });

    // Zbierz wartości przycisków (tylko stałe)
    document.querySelectorAll('button:not(.product button), input[type="submit"]').forEach(element => {
        const text = element.textContent.trim() || element.value;
        if (text && isStaticText(text) && !texts[text]) {
            texts[text] = text;
        }
    });

    // Zbierz etykiety formularzy
    document.querySelectorAll('label').forEach(element => {
        const text = element.textContent.trim();
        if (text && isStaticText(text) && !texts[text]) {
            texts[text] = text;
        }
    });

    // Zbierz nagłówki
    document.querySelectorAll('h1, h2, h3, h4, h5, h6').forEach(element => {
        const text = element.textContent.trim();
        if (text && isStaticText(text) && !texts[text]) {
            texts[text] = text;
        }
    });

    // Ręcznie dodaj kluczowe teksty, które mogły być pominięte
    const manualTexts = {
        'Magazyn Produktów': 'Magazyn Produktów',
        'Kontrolki': 'Kontrolki',
        'Dodaj nowy produkt': 'Dodaj nowy produkt',
        'Eksportuj dane': 'Eksportuj dane',
        'Importuj dane': 'Importuj dane',
        'Kurs walut': 'Kurs walut',
        'Kategorie': 'Kategorie',
        'Galeria zdjęć': 'Galeria zdjęć',
        'Sklepy': 'Sklepy',
        'Efekty': 'Efekty',
        'Pamięć': 'Pamięć',
        'Historia / Logi': 'Historia / Logi',
        'Wyczyść wszystkie dane': 'Wyczyść wszystkie dane',
        'Zapisz wszystko': 'Zapisz wszystko',
        'Ustawienia': 'Ustawienia',
        'Instrukcja': 'Instrukcja',
        'Statystyki magazynu': 'Statystyki magazynu',
        'Wartość magazynu (podczas zakupu)': 'Wartość magazynu (podczas zakupu)',
        'Wartość zakupu produktów': 'Wartość zakupu produktów',
        'Aktualna wartość produktów': 'Aktualna wartość produktów',
        'Różnica wartości': 'Różnica wartości',
        'Łączny zysk': 'Łączny zysk',
        'Łączna strata': 'Łączna strata',
        'Produkty z ceną': 'Produkty z ceną',
        'Lista produktów': 'Lista produktów',
        'Filtruj po kategorii:': 'Filtruj po kategorii:',
        'Wszystkie kategorie': 'Wszystkie kategorie',
        'Sortuj:': 'Sortuj:',
        'Nazwa (A-Z)': 'Nazwa (A-Z)',
        'Nazwa (Z-A)': 'Nazwa (Z-A)',
        'Kategoria (A-Z)': 'Kategoria (A-Z)',
        'Kategoria (Z-A)': 'Kategoria (Z-A)',
        'Cena zakupu (rosnąco)': 'Cena zakupu (rosnąco)',
        'Cena zakupu (malejąco)': 'Cena zakupu (malejąco)',
        'Rok zakupu (rosnąco)': 'Rok zakupu (rosnąco)',
        'Rok zakupu (malejąco)': 'Rok zakupu (malejąco)',
        'Ilość (rosnąco)': 'Ilość (rosnąco)',
        'Ilość (malejąco)': 'Ilość (malejąco)',
        'Zysk (rosnąco)': 'Zysk (rosnąco)',
        'Zysk (malejąco)': 'Zysk (malejąco)',
        'Data dodania (rosnąco)': 'Data dodania (rosnąco)',
        'Data dodania (malejąco)': 'Data dodania (malejąco)',
        'Wpisz szukany element...': 'Wpisz szukany element...',
        'Zaznacz wiele': 'Zaznacz wiele',
        'Usuń zaznaczone': 'Usuń zaznaczone',
        'Anuluj zaznaczenie': 'Anuluj zaznaczenie',
        'Magazyn jest pusty': 'Magazyn jest pusty',
        'Dodaj pierwszy produkt korzystając z przycisku "Dodaj nowy produkt" w sekcji Kontrolki lub sidebar.': 'Dodaj pierwszy produkt korzystając z przycisku "Dodaj nowy produkt" w sekcji Kontrolki lub sidebar.'
    };

    // Połącz ręczne teksty z automatycznie znalezionymi
    const allTexts = { ...texts, ...manualTexts };

    // Sortuj alfabetycznie dla lepszej czytelności
    const sortedTexts = {};
    Object.keys(allTexts).sort().forEach(key => {
        sortedTexts[key] = allTexts[key];
    });

    // Stwórz i pobierz plik JSON
    const dataStr = JSON.stringify(sortedTexts, null, 2);
    const dataBlob = new Blob([dataStr], {type: 'application/json'});

    const url = URL.createObjectURL(dataBlob);
    const link = document.createElement('a');
    link.href = url;
    link.download = 'translation_base_PL.json';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);

    console.log('Wyeksportowano teksty do tłumaczenia:', sortedTexts);
    showNotification('Stałe teksty wyeksportowane do pliku JSON', 'success');

    return sortedTexts;
}
