class PokeballLoader {
	constructor(elementId) {
		this.element = document.getElementById(elementId);
		this.textElement	 = this.element?.querySelector('.loader-text')	|| null;
		this.pokeballElement = this.element?.querySelector('.pokeball')		|| null;
		this.progress		= 0;
		this.isAnimating	 = false;
	}
	start(progress = 0) {
		this.isAnimating = true;
		if (this.element) {
			this.element.style.display = 'flex';
			this.element.classList.add('loading-active');
			this.element.classList.remove('loading-complete');
		}
		document.body.classList.add('loading-active');
		this.update(progress);
		return this;
	}
	update(progress) {
		if (!this.isAnimating) return this;
		this.progress = Math.max(0, Math.min(100, progress));
		if (this.textElement) {
			this.textElement.textContent = `${Math.round(this.progress)}%`;
		}
		return this;
	}
	complete() {
		if (this.element) {
			this.element.classList.remove('loading-active');
			this.element.classList.add('loading-complete');
		}
		this.update(100);
		return this;
	}
	reset() {
		this.progress = 0;
		if (this.textElement) {
			this.textElement.textContent = '0%';
		}
		if (this.element) {
			this.element.style.display = 'none';
			this.element.classList.remove('loading-active', 'loading-complete');
		}
		document.body.classList.remove('loading-active');
		this.isAnimating = false;
		return this;
	}
}
class PriceHistory {
  constructor(dbName = 'product_db', storeName = 'price_history') {
    this.dbName = dbName;
    this.storeName = storeName;
    this.dbPromise = this.openDB();
  }
  async openDB() {
    return new Promise((resolve, reject) => {
      const request = indexedDB.open(this.dbName, 3);
      request.onupgradeneeded = (event) => {
        const db = event.target.result;
        const oldVersion = event.oldVersion;
        if (oldVersion < 1) {
          const store = db.createObjectStore(this.storeName, {
            keyPath: 'id',
            autoIncrement: true
          });
          store.createIndex('productId', 'productId', { unique: false });
        }
        if (oldVersion < 2) {
          const transaction = event.target.transaction;
          const store = transaction.objectStore(this.storeName);

          if (!store.indexNames.contains('date')) {
            store.createIndex('date', 'date', { unique: false });
          }
        }
        if (oldVersion < 3) {
          logEvent("Migracja do wersji 3 bazy danych");
        }
      };
      request.onsuccess = (event) => {
        const db = event.target.result;
        this.migrateOldRecords(db).then(() => {
          resolve(db);
        }).catch(error => {
          logEvent('Błąd migracji rekordów:', error);
          resolve(db);
        });
      };

      request.onerror = (event) => reject(event.target.error);
    });
  }
  async migrateOldRecords(db) {
    return new Promise((resolve, reject) => {
      const tx = db.transaction(this.storeName, 'readwrite');
      const store = tx.objectStore(this.storeName);
      const request = store.getAll();
      request.onsuccess = async () => {
        const records = request.result;
        let updatedCount = 0;
        let needsHistoricalUpdate = false;
        for (const record of records) {
          let shouldUpdate = false;
          if (record.exchangeRate === undefined) {
            record.exchangeRate = exchangeRates[record.currency] || 1;
            record.needsHistoricalUpdate = true;
            shouldUpdate = true;
            needsHistoricalUpdate = true;
          }
          if (record.needsHistoricalUpdate === undefined) {
            record.needsHistoricalUpdate = false;
            shouldUpdate = true;
          }

          if (shouldUpdate) {
            await store.put(record);
            updatedCount++;
          }
        }

        if (updatedCount > 0) {
          if (needsHistoricalUpdate) {
            this.updateHistoricalRatesInBackground();
          }
        }
        resolve();
      };

      request.onerror = () => reject(request.error);
    });
  }

  async updateHistoricalRatesInBackground() {
    setTimeout(async () => {
      try {
        const updatedCount = await this.updateHistoricalRates();
        if (updatedCount > 0) {
          showNotification(`Zaktualizowano historyczne kursy dla ${updatedCount} rekordów`, 'success');
        }
      } catch (error) {
        logEvent('Błąd aktualizacji historycznych kursów:', error);
      }
    }, 5000);
  }

  async getHistoricalExchangeRate(currency, date) {
    if (currency === 'PLN') return 1;
    const formattedDate = date.toISOString().split('T')[0];
    try {
      const cacheKey = `historical_rate_${currency}_${formattedDate}`;
      const cachedRate = localStorage.getItem(cacheKey);

      if (cachedRate) {
        return parseFloat(cachedRate);
      }
      const response = await fetch(`https://api.nbp.pl/api/exchangerates/rates/a/${currency}/${formattedDate}/?format=json`);
      if (!response.ok) {
        return await this.getLastAvailableRate(currency, date);
      }
      const data = await response.json();
      const rate = data.rates[0].mid;
      localStorage.setItem(cacheKey, rate.toString());
      return rate;
    } catch (error) {
      logEvent(`Błąd pobierania historycznego kursu dla ${currency} z dnia ${formattedDate}:`, error);
      return await this.getLastAvailableRate(currency, date) || exchangeRates[currency] || 1;
    }
  }
  async getLastAvailableRate(currency, date) {
    for (let i = 1; i <= 7; i++) {
      const previousDate = new Date(date);
      previousDate.setDate(previousDate.getDate() - i);

      const formattedDate = previousDate.toISOString().split('T')[0];
      const cacheKey = `historical_rate_${currency}_${formattedDate}`;
      const cachedRate = localStorage.getItem(cacheKey);

      if (cachedRate) {
        return parseFloat(cachedRate);
      }
      try {
        const response = await fetch(`https://api.nbp.pl/api/exchangerates/rates/a/${currency}/${formattedDate}/?format=json`);

        if (response.ok) {
          const data = await response.json();
          const rate = data.rates[0].mid;
          localStorage.setItem(cacheKey, rate.toString());
          return rate;
        }
      } catch (error) {
        logEvent(`Błąd pobierania kursu z dnia ${formattedDate}:`, error);
      }
    }
    return null;
  }
  async getPriceHistory(productId) {
    const db = await this.dbPromise;
    return new Promise((resolve, reject) => {
      const tx = db.transaction(this.storeName, 'readonly');
      const store = tx.objectStore(this.storeName);
      const index = store.index('productId');
      const request = index.getAll(IDBKeyRange.only(productId));
      request.onsuccess = () => {
        const data = request.result
          .map(item => ({ 
            id: item.id,
            date: new Date(item.date), 
            price: item.price,
            currency: item.currency,
            exchangeRate: item.exchangeRate,
            needsHistoricalUpdate: item.needsHistoricalUpdate || false
          }))
          .sort((a, b) => a.date - b.date);
        resolve(data);
      };
      request.onerror = () => reject(request.error);
    });
  }
  async addPricePoint(productId, price, currency, date = new Date()) {
    const db = await this.dbPromise;
    const exchangeRate = exchangeRates[currency];
    return new Promise((resolve, reject) => {
      const tx = db.transaction(this.storeName, 'readwrite');
      const store = tx.objectStore(this.storeName);
      const request = store.add({
        id: generateUniqueId(),
        productId,
        price,
        currency,
        exchangeRate,
        needsHistoricalUpdate: false,
        date: date.toISOString()
      });
      request.onsuccess = () => resolve(request.result);
      request.onerror = () => reject(request.error);
    });
  }
  async updateHistoricalRates() {
    const db = await this.dbPromise;
    return new Promise((resolve, reject) => {
      const tx = db.transaction(this.storeName, 'readwrite');
      const store = tx.objectStore(this.storeName);
      const request = store.getAll();
      request.onsuccess = async () => {
        const records = request.result;
        let updatedCount = 0;
        for (const record of records) {
          if (record.needsHistoricalUpdate) {
            const historicalRate = await this.getHistoricalExchangeRate(
              record.currency, 
              new Date(record.date)
            );
            if (historicalRate && historicalRate !== record.exchangeRate) {
              record.exchangeRate = historicalRate;
              record.needsHistoricalUpdate = false;
              await store.put(record);
              updatedCount++;
            }
          }
        }
        resolve(updatedCount);
      };
      request.onerror = () => reject(request.error);
    });
  }
}
