function convertToPLN(amount, currency) {
    if (currency === 'PLN') {
        return amount;
    }
    if (!exchangeRates[currency]) {
        logEvent(`Nieznana waluta: ${currency}. Dostępne kursy:`, exchangeRates);
        throw new Error(`Nieznana waluta: ${currency}`);
    }
    const rate = exchangeRates[currency];
    if (isNaN(rate) || rate <= 0) {
        logEvent(`Nieprawidłowy kurs wymiany dla ${currency}: ${rate}`);
        throw new Error(`Nieprawidłowy kurs wymiany dla ${currency}`);
    }
    return amount * rate;
}
