async function migrateExistingProducts() {
  const loader = new PokeballLoader('pokeballLoader').start(0);
  try {
    for (const [index, product] of products.entries()) {
      loader.update((index / products.length) * 80);
      if (!product.id) {
        product.id = generateUniqueId();
      }
      const history = await priceHistory.getPriceHistory(product.id);
      if (history.length === 0) {
        const initialPrice = product.currentPrice !== null ? 
          product.currentPrice : product.price;
        const initialCurrency = product.currentPrice !== null ? 
          (product.currentCurrency || product.currency) : product.currency;
        await priceHistory.addPricePoint(
          product.id,
          initialPrice,
          initialCurrency,
          new Date(product.dateAdded)
        );
        const db = await priceHistory.dbPromise;
        const tx = db.transaction('price_history', 'readwrite');
        const store = tx.objectStore('price_history');
        const allPoints = await store.getAll();
        for (const point of allPoints) {
            if (!point.id) {
                point.id = generateUniqueId();
                await store.put(point);
            }
        }
      }
    }
    showNotification('Zaktualizowano historię cen dla istniejących produktów', 'success');
    playNotificationSound('success');
  } catch (error) {
    logEvent('Błąd migracji produktów:', error);
    showNotification('Błąd podczas aktualizacji historii cen', 'danger');
  } finally {
    loader.complete();
    setTimeout(() => loader.reset(), 500);
  }
}
