function applyAllFilters() {
  let result = [...products];
  if (currentFilter !== 'all') {
    result = result.filter(p => p.category === currentFilter);
  }
  if (currentSearch) {
    const term = currentSearch.toLowerCase();
    result = result.filter(p => 
      p.name.toLowerCase().includes(term) || 
      p.category.toLowerCase().includes(term) ||
      (p.effect && p.effect.toLowerCase().includes(term)) ||
      String(p.year).includes(term)
    );
  }
  result = sortList(result, currentSort);
  return result;
}
function sortList(list, criteria) {
  return [...list].sort((a, b) => {
    if (criteria === 'name-asc') return a.name.localeCompare(b.name);
    if (criteria === 'name-desc') return b.name.localeCompare(a.name);
    if (criteria === 'category-asc') return a.category.localeCompare(b.category);
    if (criteria === 'category-desc') return b.category.localeCompare(a.category);
    if (criteria === 'price-asc') return a.price - b.price;
    if (criteria === 'price-desc') return b.price - a.price;
    if (criteria === 'year-asc') return a.year - b.year;
    if (criteria === 'year-desc') return b.year - a.year;
    if (criteria === 'quantity-asc') return a.quantity - b.quantity;
    if (criteria === 'quantity-desc') return b.quantity - a.quantity;
    if (criteria === 'date-asc') return new Date(a.dateAdded) - new Date(b.dateAdded);
    if (criteria === 'date-desc') return new Date(b.dateAdded) - new Date(a.dateAdded);
    if (criteria.includes('profit')) {
      const calcProfit = (p) => {
        if (!p.currentPrice) return 0;
        const purchasePLN = convertToPLN(p.price, p.currency);
        const currentPLN = convertToPLN(p.currentPrice, p.currentCurrency || p.currency);
        return (currentPLN - purchasePLN) * p.quantity;
      };
      const profitA = calcProfit(a);
      const profitB = calcProfit(b);
      return criteria === 'profit-asc' ? profitA - profitB : profitB - profitA;
    }
    return 0;
  });
}
