function renderLogs() {
  const logsList = document.getElementById('logsList');
  logsList.innerHTML = '';
  const reversedLogs = [...eventLogs].reverse();
  const startIndex = (logsCurrentPage - 1) * logsPerPage;
  const endIndex = Math.min(startIndex + logsPerPage, reversedLogs.length);
  const logsToShow = reversedLogs.slice(startIndex, endIndex);

  if (logsToShow.length === 0) {
    logsList.innerHTML = '<div class="log-entry">Brak zdarzeń w historii</div>';
  } else {
    logsToShow.forEach(log => {
      const logEntry = document.createElement('div');
      logEntry.className = 'log-entry';
      const dateObj = new Date(log.timestamp);
      const formattedDate = dateObj.toLocaleString('pl-PL', {
        year: 'numeric',
        month: '2-digit',
        day: '2-digit',
        hour: '2-digit',
        minute: '2-digit',
        second: '2-digit'
      });
      logEntry.innerHTML = `
        <span class="log-date">${formattedDate}</span>
        <span class="log-message">${log.message}</span>
      `;
      logsList.appendChild(logEntry);
    });
  }
  renderLogsPagination(reversedLogs.length);
}
function renderLogsPagination(totalLogs) {
  const paginationContainer = document.getElementById('logsPagination');
  if (!paginationContainer) return;

  paginationContainer.innerHTML = '';
  const totalPages = Math.ceil(totalLogs / logsPerPage);

  const prevButton = document.createElement('button');
  prevButton.innerHTML = '<i class="fas fa-chevron-left" style="color: white;"></i>';
  prevButton.disabled = logsCurrentPage === 1;
  prevButton.addEventListener('click', () => {
    if (logsCurrentPage > 1) {
      logsCurrentPage--;
      renderLogs();
    }
  });

  const nextButton = document.createElement('button');
  nextButton.innerHTML = '<i class="fas fa-chevron-right" style="color: white;"></i>';
  nextButton.disabled = logsCurrentPage === totalPages;
  nextButton.addEventListener('click', () => {
    if (logsCurrentPage < totalPages) {
      logsCurrentPage++;
      renderLogs();
    }
  });

  const pageInfo = document.createElement('span');
  pageInfo.className = 'page-info';
  pageInfo.textContent = `Strona ${logsCurrentPage} z ${totalPages}`;

  paginationContainer.appendChild(prevButton);
  paginationContainer.appendChild(pageInfo);
  paginationContainer.appendChild(nextButton);
}
