function renderEffectsList() {
  const list = document.getElementById('effectsList');
  list.innerHTML = '';
  const startIndex = (effectsCurrentPage - 1) * paginationConfig.effectsPerPage;
  const endIndex = Math.min(startIndex + paginationConfig.effectsPerPage, effects.length);
  const pageEffects = effects.slice(startIndex, endIndex);

  if (pageEffects.length === 0) {
    list.innerHTML = '<p class="empty-state">Brak efektów</p>';
    renderEffectsPagination();
    return;
  }

  pageEffects.forEach((effect, index) => {
    const globalIndex = startIndex + index;
    const isEditing = globalIndex === editingEffectIndex;
    const isDefault = isDefaultEffect(effect);
    const item = document.createElement('div');
    item.className = 'effect-item';

    if (isEditing) {
      item.innerHTML = `
        <input type="text" class="edit-effect-name" value="${effect}" placeholder="Nowa nazwa efektu">
        <div class="buttons">
          <button class="btn-success" onclick="saveEffectEdit(${globalIndex})">
