async function checkAndUpdatePriceHistoryOnRateChange() {
    try {
        const today = new Date().toISOString().split('T')[0];
        const productsToUpdate = [];
        for (const product of products) {
            if (product.currentPrice !== null && product.currentPrice !== undefined) {
                const currency = product.currentCurrency || product.currency;
                const currentRate = exchangeRates[currency];
                if (currentRate) {
                    const history = await priceHistory.getPriceHistory(product.id);
                    const todayEntry = history.find(entry => {
                        const entryDate = new Date(entry.date).toISOString().split('T')[0];
                        return entryDate === today;
                    });
                    if (todayEntry) {
                        const entryRate = todayEntry.exchangeRate;
                        if (entryRate !== currentRate) {
                            productsToUpdate.push({
                                productId: product.id,
                                price: product.currentPrice,
                                currency: currency,
                                rate: currentRate
                            });
                        }
                    } else {
                        productsToUpdate.push({
                            productId: product.id,
                            price: product.currentPrice,
                            currency: currency,
                            rate: currentRate
                        });
                    }
                }
            }
        }
        for (const item of productsToUpdate) {
            await priceHistory.addPricePoint(
                item.productId,
                item.price,
                item.currency,
                new Date()
            );
        }
        if (productsToUpdate.length > 0) {
        }
    } catch (error) {
        logEvent('Błąd podczas aktualizacji historii cen:', error);
    }
}
