  paginationContainer.innerHTML = '';
  const totalPages = Math.ceil(effects.length / paginationConfig.effectsPerPage);
  const prevBtn = document.createElement('button');
  prevBtn.innerHTML = '<i class="fas fa-chevron-left" style="color: white;"></i>';
  prevBtn.disabled = effectsCurrentPage === 1;
  prevBtn.addEventListener('click', () => {
    if (effectsCurrentPage > 1) {
      effectsCurrentPage--;
      renderEffectsList();
    }
  });
  const nextBtn = document.createElement('button');
  nextBtn.innerHTML = '<i class="fas fa-chevron-right" style="color: white;"></i>';
  nextBtn.disabled = effectsCurrentPage === totalPages;
  nextBtn.addEventListener('click', () => {
    if (effectsCurrentPage < totalPages) {
      effectsCurrentPage++;
      renderEffectsList();
    }
  });
  const pageInfo = document.createElement('span');
  pageInfo.textContent = `Strona ${effectsCurrentPage} z ${totalPages}`;
  paginationContainer.appendChild(prevBtn);
  paginationContainer.appendChild(pageInfo);
  paginationContainer.appendChild(nextBtn);
