function isPriceOutdated(product) {
  if (!product.currentPrice || !product.lastPriceUpdate) {
    return false;
  }
  try {
    const lastUpdate = new Date(product.lastPriceUpdate);
    const sevenDaysAgo = new Date();
    sevenDaysAgo.setDate(sevenDaysAgo.getDate() - 7);
    return lastUpdate < sevenDaysAgo;
  } catch (e) {
    logEvent('Błąd sprawdzania daty ceny:', e);
    return false;
  }
}
