function cleanupOldPriceTrendCache() {
    const now = Date.now();
    let cleanedCount = 0;

    for (const productName in priceTrendCache) {
        const cached = priceTrendCache[productName];
        if ((now - cached.timestamp) > PRICE_TREND_CACHE_DURATION * 2) {
            delete priceTrendCache[productName];
            cleanedCount++;
        }
    }

    if (cleanedCount > 0) {
        localStorage.setItem('priceTrendCache', JSON.stringify(priceTrendCache));
        logEvent(`Usunięto ${cleanedCount} starych wpisów z cache Price Trend`);
