function initSidebar() {
  var sidebar = document.getElementById('sidebar');
  var overlay = document.getElementById('sidebarOverlay');
  var hamburger = document.getElementById('hamburger');
  var closeBtn = document.getElementById('closeSidebar');
  if (!sidebar) return;
  if (hamburger) {
    hamburger.addEventListener('click', toggleSidebar);
  }
  if (closeBtn) {
    closeBtn.addEventListener('click', function () {
      sidebar.classList.remove('show');
      sidebar.style.display = 'none';
      if (overlay) overlay.classList.remove('show');
      if (hamburger) hamburger.classList.remove('open');
    });
  }
  if (overlay) {
    overlay.addEventListener('click', function (e) {
      if (e.target === overlay) {
        sidebar.classList.remove('show');
        sidebar.style.display = 'none';
        overlay.classList.remove('show');
        if (hamburger) hamburger.classList.remove('open');
      }
    });
  }
  document.addEventListener('click', function (e) {
    if (!sidebar.classList.contains('show')) return;
    var clickedInside = sidebar.contains(e.target);
    var clickedHamburger = hamburger && hamburger.contains(e.target);
    if (!clickedInside && !clickedHamburger) {
      sidebar.classList.remove('show');
      sidebar.style.display = 'none';
      if (overlay) overlay.classList.remove('show');
      if (hamburger) hamburger.classList.remove('open');
    }
  });
  var menuBtns = sidebar.querySelectorAll('.sidebar-menu button, .sidebar-menu a');
  menuBtns.forEach(function (btn) {
    btn.addEventListener('click', function () {
      sidebar.classList.remove('show');
      sidebar.style.display = 'none';
      if (overlay) overlay.classList.remove('show');
      if (hamburger) hamburger.classList.remove('open');
    });
  });
}
function toggleSidebar() {
  var sidebar = document.getElementById('sidebar');
  var overlay = document.getElementById('sidebarOverlay');
  var hamburger = document.getElementById('hamburger');
  if (!sidebar) return;
  if (sidebar.classList.contains('show')) {
    sidebar.classList.remove('show');
    sidebar.style.display = 'none';
    if (overlay) overlay.classList.remove('show');
    if (hamburger) hamburger.classList.remove('open');
  } else {
    sidebar.style.display = 'block';
    sidebar.classList.add('show');
    if (overlay) overlay.classList.add('show');
    if (hamburger) hamburger.classList.add('open');
  }
}
