function openCurrencyEdit(code) {
    const baseCurrencies = ['PLN', 'USD', 'EUR'];
    const nbpCurrencies = Object.keys(exchangeRates)
        .filter(c => c !== 'PLN' && c !== 'USD' && c !== 'EUR' && c !== 'lastUpdated');

    if (baseCurrencies.includes(code) || nbpCurrencies.includes(code)) {
        showNotification('Nie można edytować waluty ' + code + ' (dostarczana przez NBP)', 'warning');
        playNotificationSound('alert');
        return;
    }

    editingCurrencyCode = code;
    document.getElementById('editCurrencyCode').value = code;
    document.getElementById('editCurrencySymbol').value = currencySymbols[code] || '';
    document.getElementById('editCurrencyRate').value = exchangeRates[code] || '';
    document.getElementById('currencyEditModal').classList.add('show');
}
function saveCurrencyEdit() {
	const newCode = document.getElementById('editCurrencyCode').value.trim().toUpperCase();
	const newSymbol = document.getElementById('editCurrencySymbol').value.trim();
	const newRate = parseFloat(document.getElementById('editCurrencyRate').value);
	if (!newCode || newCode.length !== 3) {
		showNotification('Kod waluty musi składać się z 3 liter', 'warning');
		return;
	}
	if (!newSymbol) {
		showNotification('Symbol waluty jest wymagany', 'warning');
		return;
	}
	if (isNaN(newRate) || newRate <= 0) {
		showNotification('Nieprawidłowy kurs wymiany', 'warning');
		return;
	}
	if (newCode !== editingCurrencyCode) {
		delete exchangeRates[editingCurrencyCode];
		delete currencySymbols[editingCurrencyCode];

		exchangeRates[newCode] = newRate;
		currencySymbols[newCode] = newSymbol;
	} else {
		exchangeRates[newCode] = newRate;
		currencySymbols[newCode] = newSymbol;
	}
	saveExchangeRates();
	closeCurrencyEditModal();
	openExchangeModal();
	showNotification(`Zaktualizowano walutę ${newCode}`, 'success');
	playNotificationSound('success');
}
