async function updateGalleryFromGitHub() {
    const loader = new PokeballLoader('pokeballLoader').start(0);
    loader.update(10);

    try {
        const files = await fetchGitHubFiles('images/gallery');
        if (!files) {
            throw new Error('Brak plików w galerii na GitHub');
        }

        let addedCount = 0;
        let updatedCount = 0;
        let skippedCount = 0;

        const imageExtensions = ['.png', '.jpg', '.jpeg', '.gif', '.bmp', '.webp', '.svg'];
        const imageFiles = files.filter(file => {
            const fileName = file.name.toLowerCase();
            return imageExtensions.some(ext => fileName.endsWith(ext));
        });

        for (let i = 0; i < imageFiles.length; i++) {
            const file = imageFiles[i];
            loader.update(10 + (i / imageFiles.length) * 80);

            const fileName = file.name.replace(/\.[^/.]+$/, "");

            const existingImageIndex = galleryImages.findIndex(img => 
                img.name.toLowerCase().trim() === fileName.toLowerCase().trim() && 
                img.source === 'github'
            );

            if (existingImageIndex !== -1) {
                galleryImages[existingImageIndex].url = file.download_url;
                galleryImages[existingImageIndex].dateAdded = new Date().toISOString();
                updatedCount++;
            } else {
                galleryImages.push({
                    id: generateUniqueId(),
                    name: fileName,
                    url: file.download_url,
                    dateAdded: new Date().toISOString(),
                    source: 'github'
                });
                addedCount++;
            }
        }

        if (addedCount > 0 || updatedCount > 0) {
            await saveGallery();
            renderGallery();
            updateGalleryCount();

            loader.complete();
            setTimeout(() => loader.reset(), 500);

            let message = '';

