async function removeDuplicatePricePoints() {
    const loader = new PokeballLoader('pokeballLoader');
    loader.start(0);
    loader.update(10);
    try {
        let totalRemoved = 0;
        for (const product of products) {
            const history = await priceHistory.getPriceHistory(product.id);
            const uniqueMap = new Map();
            const toRemove = [];
            for (const point of history) {
                const dateKey = new Date(point.date).toISOString().split('T')[0];
                const priceKey = parseFloat(point.price).toFixed(2);
                const key = `${dateKey}-${priceKey}-${point.currency}`;
                if (uniqueMap.has(key)) {
                    toRemove.push(point.id);
                    totalRemoved++;
                } else {
                    uniqueMap.set(key, point);
                }
            }
            if (toRemove.length > 0) {
                const db = await priceHistory.dbPromise;
                const tx = db.transaction('price_history', 'readwrite');
                const store = tx.objectStore('price_history');
                for (const id of toRemove) {
                    await store.delete(id);
                }
                await tx.done;
            }
        }
        if (totalRemoved > 0) {
            logEvent(`Usunięto ${totalRemoved} zduplikowanych punktów cenowych.`);
        }
    } catch (error) {
        logEvent('Błąd podczas usuwania duplikatów punktów cenowych:', error);
        logEvent(`Błąd usuwania duplikatów punktów cenowych: ${error.message}`);
    } finally {
        loader.complete();
        setTimeout(() => loader.reset(), 500);
    }
}
