async function openDB() {
	return new Promise((resolve, reject) => {
		const request = indexedDB.open('ProductGalleryDB', 10);
		request.onupgradeneeded = function(event) {
			const db = event.target.result;
			const oldVersion = event.oldVersion;
			if (oldVersion < 1) {
				if (!db.objectStoreNames.contains('productImages')) {
					db.createObjectStore('productImages', { keyPath: 'id' });
				}
				if (!db.objectStoreNames.contains('galleryImages')) {
					db.createObjectStore('galleryImages', { keyPath: 'id' });
				}
			}
            if (!db.objectStoreNames.contains('backgroundImages')) {
                db.createObjectStore('backgroundImages', { keyPath: 'id' });
            }
		};
		request.onsuccess = function(event) {
			resolve(event.target.result);
		};
		request.onerror = function(event) {
			reject('Błąd otwierania bazy danych: ' + event.target.error);
		};
	});
}
async function loadBackgrounds() {
    try {
        const db = await openDB();
        const tx = db.transaction('backgroundImages', 'readonly');
        const store = tx.objectStore('backgroundImages');
        const request = store.getAll();
        return new Promise((resolve, reject) => {
            request.onsuccess = function() {
                const savedImages = request.result || [];
                backgroundImages = [];

                for (const savedImg of savedImages) {
                    if (savedImg.source === 'github') {
                        backgroundImages.push({
                            id: savedImg.id,
                            name: savedImg.name,
                            url: savedImg.url,
                            dateAdded: savedImg.dateAdded,
                            source: 'github'
                        });
                    } else {
                        backgroundImages.push({
                            id: savedImg.id,
                            name: savedImg.name,
                            data: savedImg.data,
                            dateAdded: savedImg.dateAdded,
                            source: 'local'
                        });
                    }
                }

                updateBackgroundCount();
                cleanupGalleryDuplicatesAutomatically();
                resolve();
            };
            request.onerror = () => reject('Błąd ładowania tła');
        });
    } catch (error) {
        logEvent('Błąd ładowania tła:', error);
        backgroundImages = [];
        updateBackgroundCount();
    }
}
async function saveBackgrounds() {
    try {
        const db = await openDB();
        const tx = db.transaction('backgroundImages', 'readwrite');
        const store = tx.objectStore('backgroundImages');
        await store.clear();

        for (const img of backgroundImages) {
            if (!img.id) {
                img.id = generateUniqueId();
            }

            const imageToSave = {
                id: img.id,
                name: img.name,
                dateAdded: img.dateAdded,
                source: img.source || 'local'
            };

            if (img.source === 'github') {
                imageToSave.url = img.url;
            } else {
                imageToSave.data = img.data;
            }

            await store.put(imageToSave);
        }
        return new Promise(resolve => tx.oncomplete = resolve);
    } catch (error) {
        logEvent('Błąd zapisu tła:', error);
    }
}
