function backgroundImageExists(fileName) {
    const normalizedName = fileName.toLowerCase().trim().replace(/\s+/g, ' ');
    const existingBackground = backgroundImages.find(bg => 
        bg.name.toLowerCase().trim().replace(/\s+/g, ' ') === normalizedName
    );
    if (existingBackground && isFromGitHub(existingBackground)) {
        return false;
    }
    return !existingBackground;
}
function renderBackgroundPagination() {
    const container = document.getElementById('backgroundModal').querySelector('.modal-content');
    const oldPagination = container.querySelector('.pagination-container');
    if (oldPagination) oldPagination.remove();
    if (backgroundTotalPages <= 1) return;
    const pagination = document.createElement('div');
    pagination.className = 'pagination-container';
    const prevBtn = document.createElement('button');
    prevBtn.innerHTML = '<i class="fas fa-chevron-left"></i>';
    prevBtn.disabled = backgroundCurrentPage === 1;
    prevBtn.addEventListener('click', () => {
        if (backgroundCurrentPage > 1) {
            backgroundCurrentPage--;
            renderBackgrounds();
        }
    });
    const nextBtn = document.createElement('button');
    nextBtn.innerHTML = '<i class="fas fa-chevron-right"></i>';
    nextBtn.disabled = backgroundCurrentPage === backgroundTotalPages;
    nextBtn.addEventListener('click', () => {
        if (backgroundCurrentPage < backgroundTotalPages) {
            backgroundCurrentPage++;
            renderBackgrounds();
        }
    });
    const pageInfo = document.createElement('span');
    pageInfo.textContent = `Strona ${backgroundCurrentPage} z ${backgroundTotalPages}`;
    pagination.appendChild(prevBtn);
    pagination.appendChild(pageInfo);
    pagination.appendChild(nextBtn);
    container.appendChild(pagination);
}
