async function fetchGoldPrice() {
    try {
        const response = await fetch('https://api.nbp.pl/api/cenyzlota');
        if (!response.ok) {
            throw new Error('Błąd pobierania ceny złota');
        }
        const data = await response.json();
        const price = data[0].cena;
        exchangeRates.GOLD = price;
        saveExchangeRates();
        return price;
    } catch (error) {
        logEvent('Błąd pobierania ceny złota:', error);
        return exchangeRates.GOLD || null;
    }
}
async function calculateGoldBuybackPrice(product, forceUpdate = false) {
  try {
    const now = new Date();
    if (!forceUpdate && lastGoldBuybackUpdate && (now - lastGoldBuybackUpdate) < 300000) {
      return {
        buybackPrice: (product.currentPrice * 0.98) * product.quantity,
        rateInfo: `Kurs z dnia ${lastGoldBuybackUpdate.toLocaleDateString('pl-PL')} ${lastGoldBuybackUpdate.toLocaleTimeString('pl-PL')} (dane z pamięci podręcznej)`,
        usdRate: exchangeRates.USD || 4.0
      };
    }
    const response = await fetch('https://api.nbp.pl/api/exchangerates/rates/a/usd/?format=json');
    if (!response.ok) {
      throw new Error('Nie udało się pobrać kursu USD');
    }
    const data = await response.json();
    const usdPlnRate = data.rates[0].mid;
    const rateDate = new Date(data.rates[0].effectiveDate);
    const rateTime = new Date().toLocaleTimeString('pl-PL');
    exchangeRates.USD = usdPlnRate;
    exchangeRates.lastUpdated = new Date().toISOString();
    saveExchangeRates();
    lastGoldBuybackUpdate = new Date();
    const totalBuybackPrice = (product.currentPrice * 0.98) * product.quantity;
    return {
      buybackPrice: totalBuybackPrice,
      rateInfo: `Kurs z dnia ${rateDate.toLocaleDateString('pl-PL')} ${rateTime}`,
      usdRate: usdPlnRate
    };
  } catch (error) {
    logEvent('Błąd obliczania ceny odkupu:', error);
    return {
      buybackPrice: (product.currentPrice * 0.98) * product.quantity,
      rateInfo: `Kurs z dnia ${exchangeRates.lastUpdated ? new Date(exchangeRates.lastUpdated).toLocaleDateString('pl-PL') : 'brak danych'} (używane zapisane dane)`,
      usdRate: exchangeRates.USD || 4.0
    };
  }
}
async function fetchLBMAGoldPrice() {
    try {
        const nbpGoldPricePLN = await fetchGoldPrice();
        const usdToPlnRate = exchangeRates.USD;
        const lbmaPriceUSD = nbpGoldPricePLN / usdToPlnRate;
        return lbmaPriceUSD;
    } catch (error) {
        logEvent('Błąd pobierania ceny LBMA:', error);
        return 1800;
    }
}
async function fetchUSDAverageRate() {
    try {
        const response = await fetch('https://api.nbp.pl/api/exchangerates/rates/a/usd/?format=json');
        if (!response.ok) {
            throw new Error('Błąd pobierania kursu USD');
        }
        const data = await response.json();
        return data.rates[0].mid;
    } catch (error) {
        logEvent('Błąd pobierania kursu USD:', error);
        return exchangeRates.USD || 4.0;
    }
}
