async function uploadToBackgroundGallery() {
    const input = document.getElementById('backgroundUploadInput');
    if (!input.files || !input.files[0]) return;

    const file = input.files[0];
    const fileName = document.getElementById('backgroundImageName').value.trim() || 
                     file.name.replace(/\.[^/.]+$/, "");
    const nameExists = backgroundImages.some(bg => 
        bg.name.toLowerCase() === fileName.toLowerCase()
    );
    if (nameExists) {
        showNotification('Tło o tej nazwie już istnieje', 'warning');
        return;
    }
    const reader = new FileReader();
    reader.onload = async function(e) {
        backgroundImages.push({
            id: generateUniqueId(),
            name: fileName,
            data: e.target.result,
            dateAdded: new Date().toISOString()
        });
