function initModalScrollButtons() {
    const modals = document.querySelectorAll('.modal');

    modals.forEach(modal => {
        let scrollButton = modal.querySelector('.modal-scroll-to-top');

        if (!scrollButton) {
            scrollButton = document.createElement('button');
            scrollButton.className = 'modal-scroll-to-top';
            scrollButton.innerHTML = '↑';
            scrollButton.title = 'Powrót do góry';
            modal.appendChild(scrollButton);

            scrollButton.addEventListener('click', function() {
                const modalContent = modal.querySelector('.modal-content');
                if (modalContent) {
                    modalContent.scrollTo({
                        top: 0,
                        behavior: 'smooth'
                    });
                }
            });
        }

        const scrollableElement = modal.querySelector('.modal-content') || modal;

        scrollableElement.addEventListener('scroll', function() {
            if (this.scrollTop > 50) {
                scrollButton.classList.add('show');
            } else {
                scrollButton.classList.remove('show');
            }
        });

        const observer = new MutationObserver(function(mutations) {
            mutations.forEach(function(mutation) {
                if (mutation.attributeName === 'class') {
                    if (modal.classList.contains('show')) {
                        const content = modal.querySelector('.modal-content');
                        if (content) {
                            content.scrollTop = 0;
                        }
                        scrollButton.classList.remove('show');
                    }
                }
            });
        });

        observer.observe(modal, { attributes: true });
    });
}
document.addEventListener('DOMContentLoaded', function() {
    initModalScrollButtons();
});
function updateMainFormFields() {
    const category = document.getElementById('category').value;
    const conditionField = document.getElementById('conditionField');
    const languageField = document.getElementById('languageField');
    const effectField = document.getElementById('effectField');
    const currentPriceGroup = document.getElementById('currentPriceGroup');
    const quantityLabel = document.getElementById('quantityLabel');
    const priceLabel = document.getElementById('priceLabel');
    const conditionSelect = document.getElementById('condition');
    const languageSelect = document.getElementById('language');

    conditionField.style.display = 'none';
    languageField.style.display = 'none';
    effectField.style.display = 'none';

    if (conditionSelect) conditionSelect.required = false;
    if (languageSelect) languageSelect.required = false;

    if (category === 'Singles') {
        conditionField.style.display = 'block';
        effectField.style.display = 'block';
        if (conditionSelect) conditionSelect.required = true;
    }

    if (!['Złoto', 'Akcesoria', 'Inne'].includes(category)) {
        languageField.style.display = 'block';
        if (languageSelect) languageSelect.required = true;
    } else {
        languageField.style.display = 'none';
        if (languageSelect) languageSelect.required = false;
    }

    if (category === 'Złoto') {
        if (currentPriceGroup) currentPriceGroup.style.display = 'none';
        if (quantityLabel) quantityLabel.textContent = 'Ilość (gram) *';
        if (priceLabel) priceLabel.textContent = 'Cena zakupu za 1 gram *';
    } else {
        if (currentPriceGroup) currentPriceGroup.style.display = 'block';
        if (quantityLabel) quantityLabel.textContent = 'Ilość *';
        if (priceLabel) priceLabel.textContent = 'Cena zakupu jednej sztuki *';
    }
}
async function handleProductFormSubmit(e) {
    e.preventDefault();

    resetMainFormValidation();

    const nm = document.getElementById('name').value.trim();
    const cat = document.getElementById('category').value;
    const qt = parseInt(document.getElementById('quantity').value);
    const pr = parseFloat(document.getElementById('price').value);
    const yr = parseInt(document.getElementById('year').value);

    let cp = null;
    let currentCurrency = null;

    if (cat !== 'Złoto') {
        cp = document.getElementById('currentPrice').value;
        currentCurrency = document.getElementById('currentCurrency').value;
    }

    const currency = document.getElementById('currency').value;
    const file = document.getElementById('image').files[0];
    const effectIn = document.getElementById('effect').value;
    const conditionIn = cat === 'Singles' ? document.getElementById('condition').value : null;
    const languageIn = !['Złoto', 'Akcesoria', 'Inne'].includes(cat) ? document.getElementById('language').value : null;

    const errors = [];

    if (!nm) {
        errors.push('Nazwa produktu jest wymagana');
        highlightField(document.getElementById('name'));
    }

    if (!cat) {
        errors.push('Kategoria jest wymagana');
        highlightField(document.getElementById('category'));
    }

    if (isNaN(qt) || qt < 1) {
        errors.push('Nieprawidłowa ilość');
        highlightField(document.getElementById('quantity'));
    }

    if (isNaN(pr) || pr <= 0) {
        errors.push('Nieprawidłowa cena zakupu');
        highlightField(document.getElementById('price'));
    }

    if (isNaN(yr) || yr < 2000 || yr > 2100) {
        errors.push('Nieprawidłowy rok zakupu');
        highlightField(document.getElementById('year'));
    }

    if (!currency) {
        errors.push('Waluta jest wymagana');
        highlightField(document.getElementById('currency'));
    }

    if (cat === 'Singles' && !conditionIn) {
        errors.push('Stan karty jest wymagany dla kategorii Singles');
        highlightField(document.getElementById('condition'));
    }

    if (!['Złoto', 'Akcesoria', 'Inne'].includes(cat) && !languageIn) {
        errors.push('Język jest wymagany dla tej kategorii');
        highlightField(document.getElementById('language'));
    }

    if (cat !== 'Złoto' && cp && cp.trim() !== '') {
        const currentPriceValue = parseFloat(cp);
        if (isNaN(currentPriceValue) || currentPriceValue < 0) {
            errors.push('Nieprawidłowa aktualna cena');
            highlightField(document.getElementById('currentPrice'));
        }
        if (!currentCurrency) {
            errors.push('Waluta aktualnej ceny jest wymagana');
            highlightField(document.getElementById('currentCurrency'));
        }
    }

    if (errors.length > 0) {
        showNotification('Popraw następujące błędy: ' + errors.join(', '), 'danger');
        playNotificationSound('alert');
        return;
    }

    let currentPriceValue = null;
    if (cat !== 'Złoto' && cp && cp.trim() !== '') {
        currentPriceValue = parseFloat(cp);
    }

    let finalName = nm;

    let dataUrl = '';
    if (file) {
        dataUrl = await new Promise(resolve => {
            const rd = new FileReader();
            rd.onload = () => resolve(rd.result);
            rd.readAsDataURL(file);
        });
    }
    if (!dataUrl && selectedImageUrl) {
        dataUrl = selectedImageUrl;
    }

    const productId = generateUniqueId();
    const lastPriceUpdate = currentPriceValue !== null ? new Date().toISOString() : null;

    if (cat === 'Złoto') {
        const goldPrice = await fetchGoldPrice();
        if (goldPrice !== null) {
            currentPriceValue = goldPrice;
            currentCurrency = 'PLN';
        } else {
            showNotification('Nie udało się pobrać ceny złota. Używam ostatniej znanej ceny.', 'warning');
            currentPriceValue = exchangeRates.GOLD || pr;
            currentCurrency = 'PLN';
        }
    }

    products.push({
        id: productId,
        name: finalName,
        category: cat,
        quantity: qt,
        price: pr,
        year: yr,
        currency: currency,
        currentPrice: currentPriceValue,
        currentCurrency: currentPriceValue !== null ? currentCurrency : null,
        image: dataUrl,
        dateAdded: new Date().toISOString(),
        effect: cat === 'Singles' ? effectIn || null : null,
        condition: conditionIn,
        language: languageIn,
        lastPriceUpdate: lastPriceUpdate
    });

    if (currentPriceValue !== null) {
        try {
            await priceHistory.addPricePoint(
                productId,
                currentPriceValue,
                currentCurrency
            );
        } catch (error) {
            logEvent('Błąd zapisu początkowego wpisu historii:', error);
        }
    }

    await saveProducts();
    renderProducts(applyAllFilters());

    form.reset();
    document.getElementById('currency').value = 'PLN';
    document.getElementById('currentCurrency').value = 'PLN';
    document.getElementById('imagePreview').style.display = 'none';

    document.getElementById('conditionField').style.display = 'none';
    document.getElementById('languageField').style.display = 'none';

    const quantityLabel = document.querySelector('label[for="quantity"]');
    if (quantityLabel) quantityLabel.textContent = 'Ilość';

    showNotification('Produkt został dodany', 'success');
    playNotificationSound('success');

    closeAddNewProductModal();
}
