function toggleBackgroundSelection(index) {
    const idx = selectedBackgroundImages.indexOf(index);
    if (idx > -1) {
        selectedBackgroundImages.splice(idx, 1);
    } else {
        selectedBackgroundImages.push(index);
    }
    renderBackgrounds();
    updateBackgroundSelectionCounter();
}
function updateBackgroundSelectionCounter() {
    const counter = document.getElementById('backgroundSelectionCounter');
    if (!counter) {
        const selectionControls = document.getElementById('backgroundSelectionControls');
        const counterEl = document.createElement('div');
        counterEl.id = 'backgroundSelectionCounter';
        counterEl.className = 'selection-counter';
        counterEl.innerHTML = `<i class="fas fa-check-circle"></i> Zaznaczono: ${selectedBackgroundImages.length}`;
        selectionControls.insertBefore(counterEl, selectionControls.firstChild);
    } else {
        counter.innerHTML = `<i class="fas fa-check-circle"></i> Zaznaczono: ${selectedBackgroundImages.length}`;
    }
