async function deleteSelectedBackgrounds() {
    if (!selectedBackgroundImages.length) return;
    showConfirmation(
        'Potwierdzenie usunięcia',
        `Czy na pewno chcesz usunąć ${selectedBackgroundImages.length} zaznaczonych tła?`,
        async (confirmed) => {
            if (!confirmed) return;
            const deletedIds = selectedBackgroundImages.map(i => backgroundImages[i].id);
            if (deletedIds.includes(selectedBackgroundId)) {
                selectedBackgroundId = null;
                localStorage.removeItem('selectedBackground');
                document.body.style.backgroundImage = '';
                document.querySelector('.header-wrapper').style.backgroundColor = '';
            }
            selectedBackgroundImages.sort((a, b) => b - a).forEach(i => {
                backgroundImages.splice(i, 1);
            });

            await saveBackgrounds();
            cancelBackgroundSelection();
            renderBackgrounds();
            updateBackgroundCount();
        }
    );
}
