async function processImportFile(file) {
    const loader = new PokeballLoader('pokeballLoader').start(0);
    try {
        if (!file) return;
        loader.update(20);
        const zip = await JSZip.loadAsync(file);

        if (zip.file('settings.json')) {
            const settingsData = await zip.file('settings.json').async('text');
            const settings = JSON.parse(settingsData);

            if (settings.version >= 3) {
                paginationConfig = settings.paginationConfig || paginationConfig;

                selectedBackgroundId = settings.selectedBackground || null;

                darkMode = settings.darkMode || false;
                soundPermissionGiven = settings.soundPermission || false;
                soundAllowed = settings.soundAllowed || false;

                if (settings.version >= 4) {
                    currentFilter = settings.currentFilter || 'all';
                    currentSort = settings.currentSort || 'date-asc';
                    currentSearch = settings.currentSearch || '';
                    productsCurrentPage = settings.productsCurrentPage || 1;
                    categoriesCurrentPage = settings.categoriesCurrentPage || 1;
                    effectsCurrentPage = settings.effectsCurrentPage || 1;
                    shopsCurrentPage = settings.shopsCurrentPage || 1;
                    currenciesCurrentPage = settings.currenciesCurrentPage || 1;
                    logsCurrentPage = settings.logsCurrentPage || 1;
                    galleryCurrentPage = settings.galleryCurrentPage || 1;
                    backgroundCurrentPage = settings.backgroundCurrentPage || 1;
                }
                if (settings.version >= 5) {
                    currencyConfig = settings.currencyConfig || currencyConfig;
                    if (settings.visibilitySettings) {
                        showControlsPanel = settings.visibilitySettings.showControlsPanel !== undefined ? settings.visibilitySettings.showControlsPanel : true;
                        showHamburgerOnDesktop = settings.visibilitySettings.showHamburgerOnDesktop !== undefined ? settings.visibilitySettings.showHamburgerOnDesktop : false;
                        showInstructionButton = settings.visibilitySettings.showInstructionButton !== undefined ? settings.visibilitySettings.showInstructionButton : true;
                        showStatisticsSection = settings.visibilitySettings.showStatisticsSection !== undefined ? settings.visibilitySettings.showStatisticsSection : true;
                        showNotifications = settings.visibilitySettings.showNotifications !== undefined ? settings.visibilitySettings.showNotifications : true;
                        showAdvancedButtons = settings.visibilitySettings.showAdvancedButtons !== undefined ? settings.visibilitySettings.showAdvancedButtons : true;
                    }
                    if (settings.appearanceSettings) {
                        localStorage.setItem('panelOpacity', settings.appearanceSettings.panelOpacity);
                        localStorage.setItem('backgroundBlur', settings.appearanceSettings.backgroundBlur);
                        localStorage.setItem('darkenEffect', settings.appearanceSettings.darkenEffect);
                        localStorage.setItem('borderRadius', settings.appearanceSettings.borderRadius);
                        localStorage.setItem('borderWidth', settings.appearanceSettings.borderWidth);
                        localStorage.setItem('borderColor', settings.appearanceSettings.borderColor);
                        if (settings.appearanceSettings.customColors) {
                            localStorage.setItem('customColors', JSON.stringify(settings.appearanceSettings.customColors));
                        }
                        if (settings.appearanceSettings.appFont) {
                            localStorage.setItem('appFont', settings.appearanceSettings.appFont);
                        }
                        localStorage.setItem('animationsEnabled', settings.appearanceSettings.animationsEnabled);
                    }

                    if (settings.priceTrendCache) {
                        priceTrendCache = settings.priceTrendCache;
                        localStorage.setItem('priceTrendCache', JSON.stringify(priceTrendCache));
                    }
                }

                document.body.classList.toggle('dark-mode', darkMode);
                localStorage.setItem('paginationConfig', JSON.stringify(paginationConfig));
                localStorage.setItem('selectedBackground', selectedBackgroundId);
                localStorage.setItem('darkMode', darkMode);
                localStorage.setItem('soundPermission', soundPermissionGiven);
                localStorage.setItem('currencyConfig', JSON.stringify(currencyConfig));
                localStorage.setItem('showControlsPanel', showControlsPanel);
                localStorage.setItem('showHamburgerOnDesktop', showHamburgerOnDesktop);
                localStorage.setItem('showInstructionButton', showInstructionButton);
                localStorage.setItem('showStatisticsSection', showStatisticsSection);
                localStorage.setItem('showNotifications', showNotifications);
                localStorage.setItem('showAdvancedButtons', showAdvancedButtons);

                if (selectedBackgroundId) {
                    setTimeout(() => {
                        applyBackgroundStyle();
                    }, 100);
                }
            }
        }

        if (zip.file('price_trend_cache.json')) {
            try {
                const cacheData = await zip.file('price_trend_cache.json').async('text');
                const cacheObj = JSON.parse(cacheData);
                if (cacheObj.priceTrendCache) {
                    priceTrendCache = cacheObj.priceTrendCache;
                    localStorage.setItem('priceTrendCache', JSON.stringify(priceTrendCache));
                }
            } catch (cacheError) {
                logEvent('Błąd importu price trend cache:', cacheError);
            }
        }

        loader.update(30);

        if (zip.file('products.json')) {
            const productsData = await zip.file('products.json').async('text');
            const productsObj = JSON.parse(productsData);

            let importedProducts = [];
            if (productsObj.version === 4 && productsObj.products) {
                importedProducts = productsObj.products.map(product => {
                    if (product.imageSource === 'github' && product.imageGitHubName) {
                        product.image = null;
                        product.imageId = null;
                    }
                    return product;
                });
            } else if (productsObj.version === 3 && productsObj.products) {
                importedProducts = productsObj.products;
            } else if (productsObj.version === 2 && productsObj.products) {
                importedProducts = productsObj.products;
            } else {
                importedProducts = productsObj;
            }

            importedProducts.forEach(importedProduct => {
                if (!importedProduct.id) {
                    importedProduct.id = generateUniqueId();
                }

                const exists = products.some(p => p.id === importedProduct.id);
                if (!exists) {
                    products.push(importedProduct);
                }
            });
        }

        if (zip.file('categories.json')) {
            const categoriesData = await zip.file('categories.json').async('text');
            const importedCategories = JSON.parse(categoriesData);
            categories.push(...importedCategories);
        }

        if (zip.file('exchange.json')) {
            const exchangeData = await zip.file('exchange.json').async('text');
            const exchangeObj = JSON.parse(exchangeData);
            if (exchangeObj.rates) {
                Object.assign(exchangeRates, exchangeObj.rates);
            }
            if (exchangeObj.symbols) {
                Object.assign(currencySymbols, exchangeObj.symbols);
            }
        }

        if (zip.file('shops.json')) {
            const shopsData = await zip.file('shops.json').async('text');
            const importedShops = JSON.parse(shopsData);
            shops.push(...importedShops);
        }

        if (zip.file('effects.json')) {
            const effectsData = await zip.file('effects.json').async('text');
            const importedEffects = JSON.parse(effectsData);
            effects.push(...importedEffects);
        }

        if (zip.file('logs.json')) {
            const logsData = await zip.file('logs.json').async('text');
            const importedLogs = JSON.parse(logsData);
            eventLogs.push(...importedLogs);
        }

        loader.update(50);

        if (zip.file('price_history.json')) {
            try {
                const historyData = await zip.file('price_history.json').async('text');
                const allHistory = JSON.parse(historyData);
                const db = await priceHistory.dbPromise;
                const tx = db.transaction('price_history', 'readwrite');
                const store = tx.objectStore('price_history');

                await store.clear();

                for (const productHistory of allHistory) {
                    for (const point of productHistory.history) {
                        await store.add({
                            id: generateUniqueId(),
                            productId: productHistory.productId,
                            date: point.date,
                            price: point.price,
                            currency: point.currency,
                            exchangeRate: point.exchangeRate,
                            needsHistoricalUpdate: point.needsHistoricalUpdate || false
                        });
                    }
                }
            } catch (historyError) {
                logEvent('Błąd importu historii cen:', historyError);
            }
        }

        loader.update(70);

        if (zip.folder('images')) {
            const imgFolder = zip.folder('images');

            if (imgFolder.folder('products')) {
                const productImgFolder = imgFolder.folder('products');
                const productFiles = [];
                productImgFolder.forEach((relativePath, file) => {
                    if (!file.dir) {
                        productFiles.push(file);
                    }
                });

                logEvent(`Znaleziono ${productFiles.length} obrazów produktów`);

                for (const file of productFiles) {
                    try {
                        const base64Data = await file.async('base64');
                        const imageData = `data:image/png;base64,${base64Data}`;
                        const productId = file.name.replace('.png', '');
                        const product = products.find(p => p.id === productId);
                        if (product) {
                            product.image = imageData;
                            logEvent(`Przypisano obraz do produktu: ${product.name}`);
                        }
                    } catch (error) {
                        logEvent('Błąd importu obrazu produktu:', file.name, error);
                    }
                }
            }

            if (imgFolder.folder('gallery')) {
                const galleryFolder = imgFolder.folder('gallery');
                const galleryFiles = [];
                galleryFolder.forEach((relativePath, file) => {
                    if (!file.dir) {
                        galleryFiles.push(file);
                    }
                });

                logEvent(`Znaleziono ${galleryFiles.length} obrazów galerii`);

                for (const file of galleryFiles) {
                    try {
                        const base64Data = await file.async('base64');
                        const imageData = `data:image/png;base64,${base64Data}`;

                        let fileName = file.name;

                        if (fileName.includes('/')) {
                            const pathParts = fileName.split('/');
                            fileName = pathParts[pathParts.length - 1];
                        }

                        fileName = fileName.replace('.png', '');

                        logEvent(`Przetwarzanie pliku galerii: "${file.name}" -> "${fileName}"`);

                        const exists = galleryImages.some(img => 
                            img.name.toLowerCase() === fileName.toLowerCase() && 
                            img.source !== 'github'
                        );

                        if (!exists) {
                            galleryImages.push({
                                id: generateUniqueId(),
                                name: fileName,
                                data: imageData,
                                dateAdded: new Date().toISOString(),
                                source: 'local'
                            });
                            logEvent(`Dodano do galerii: "${fileName}"`);
                        } else {
                            logEvent(`Pominięto istniejący obraz: "${fileName}"`);
                        }
                    } catch (error) {
                        logEvent('Błąd importu obrazu galerii:', file.name, error);
                    }
                }

                await saveGallery();
                logEvent(`Zapisano galerię po imporcie`);
            }

            if (imgFolder.folder('backgrounds')) {
                const backgroundFolder = imgFolder.folder('backgrounds');
                const backgroundFiles = [];
                backgroundFolder.forEach((relativePath, file) => {
                    if (!file.dir) {
                        backgroundFiles.push(file);
                    }
                });

                logEvent(`Znaleziono ${backgroundFiles.length} obrazów tła`);

                for (const file of backgroundFiles) {
                    try {
                        const base64Data = await file.async('base64');
                        const imageData = `data:image/png;base64,${base64Data}`;

                        let fileName = file.name;

                        if (fileName.includes('/')) {
                            const pathParts = fileName.split('/');
                            fileName = pathParts[pathParts.length - 1];
                        }

                        fileName = fileName.replace('.png', '');

                        logEvent(`Przetwarzanie pliku tła: "${file.name}" -> "${fileName}"`);

                        const exists = backgroundImages.some(bg => 
                            bg.name.toLowerCase() === fileName.toLowerCase() && 
                            bg.source !== 'github'
                        );

                        if (!exists) {
                            backgroundImages.push({
                                id: generateUniqueId(),
                                name: fileName,
                                data: imageData,
                                dateAdded: new Date().toISOString(),
                                source: 'local'
                            });
                            logEvent(`Dodano tło: "${fileName}"`);
                        } else {
                            logEvent(`Pominięto istniejące tło: "${fileName}"`);
                        }
                    } catch (error) {
                        logEvent('Błąd importu tła:', file.name, error);
                    }
                }

                await saveBackgrounds();
            }
        }

        loader.update(95);

        if (zip.file('selection_state.json')) {
            try {
                const selectionData = await zip.file('selection_state.json').async('text');
                const selectionState = JSON.parse(selectionData);

                if (selectionState.selectedBackgroundId && !selectedBackgroundId) {
                    selectedBackgroundId = selectionState.selectedBackgroundId;
                    localStorage.setItem('selectedBackground', selectedBackgroundId);
                }
            } catch (selectionError) {
                logEvent('Błąd importu stanu zaznaczeń:', selectionError);
            }
        }

        await saveProducts();
        await saveCategories();
        await saveExchangeRates();
        await saveShops();
        await saveEffects();
        await saveLogs();
        await saveGallery();
        await saveBackgrounds();

        if (selectedBackgroundId) {
            setTimeout(() => {
                applyBackgroundStyle();
            }, 500);
        }

        await updateCurrencySelects();
        await updateCategorySelects();
        await fillEffectSelect();
        await renderProducts(applyAllFilters());
        await forceUpdateStatistics();
        await renderGallery();
        await renderBackgrounds();
        await removeDuplicatePricePoints();

        await restoreGitHubImagesAfterImport();

        document.getElementById('categoryFilter').value = currentFilter;
        document.getElementById('sortSelect').value = currentSort;
        document.getElementById('searchInput').value = currentSearch;

        initAllSettings();
        applyVisibilitySettings();
        applyAdditionalVisibilitySettings();
        applyAdvancedButtonsSetting();
        forceSaveAll();
        await cleanupGalleryDuplicatesAutomatically();

        loader.complete();
        setTimeout(() => {
            loader.reset();
            showNotification('Import zakończony pomyślnie', 'success');
            playNotificationSound('success');

            if (selectedBackgroundId) {
                setTimeout(() => {
                    applyBackgroundStyle();
                    showNotification('Przywrócono ustawienia tła', 'success');
                }, 1000);
            }
        }, 500);
    } catch (error) {
        loader.reset();
        showNotification('Błąd przetwarzania pliku: ' + error.message, 'danger');
    }
}
function isImageFromGitHub(imageData) {
    return galleryImages.some(img => img.data === imageData && img.source === 'github');
}
async function restoreGitHubImagesAfterImport() {
    const loader = new PokeballLoader('pokeballLoader').start(0);
    try {
        let restoredProductImages = 0;

        for (const product of products) {
            if (product.imageSource === 'github' && product.imageGitHubName && !product.image) {
                const galleryImage = galleryImages.find(img => 
                    img.name.toLowerCase().trim() === product.imageGitHubName.toLowerCase().trim() && 
                    img.source === 'github'
                );

                if (galleryImage) {
                    product.image = galleryImage.url;
                    product.imageId = galleryImage.id;
                    restoredProductImages++;
                }
            }
        }

        if (restoredProductImages > 0) {
            await saveProducts();
            renderProducts(applyAllFilters());
            showNotification(`Przywrócono ${restoredProductImages} obrazów produktów z GitHub`, 'success');
        }
    } catch (error) {
        logEvent('Błąd przywracania obrazów z GitHub:', error);
        showNotification('Część obrazów z GitHub nie została przywrócona.', 'warning');
    } finally {
        loader.reset();
    }
}
async function fetchGitHubImageByName(imageName, type = 'gallery') {
    try {
        const folderPath = type === 'background' ? 'images/backgrounds' : 'images/gallery';
        const files = await fetchGitHubFiles(folderPath);
        if (files) {
            const imageFile = files.find(file => {
                const fileName = file.name.replace(/\.[^/.]+$/, "");
                return fileName.toLowerCase() === imageName.toLowerCase();
            });

            if (imageFile) {
                return imageFile.download_url;
            }
        }
    } catch (error) {
        logEvent('Błąd pobierania obrazu z GitHub:', error);
    }
    return null;
}
function handleAndroidFileSelect(fileName, base64Data) {
    logEvent('Wybieranie pliku z Androida: ' + fileName);
    const byteCharacters = atob(base64Data);
    const byteNumbers = new Array(byteCharacters.length);
    for (let i = 0; i < byteCharacters.length; i++) {
        byteNumbers[i] = byteCharacters.charCodeAt(i);
    }
    const byteArray = new Uint8Array(byteNumbers);
    const blob = new Blob([byteArray], {type: 'application/zip'});
    const file = new File([blob], fileName, {type: 'application/zip'});
    processImportFile(file);
}
