async function cleanupGalleryDuplicatesAutomatically() {
    try {
        const originalGalleryCount = galleryImages.length;
        const originalBackgroundCount = backgroundImages.length;

        const uniqueGalleryImages = removeDuplicatesWithGitHubPriority(galleryImages);
        const uniqueBackgroundImages = removeDuplicatesWithGitHubPriority(backgroundImages);

        const galleryChanged = originalGalleryCount !== uniqueGalleryImages.length;
        const backgroundChanged = originalBackgroundCount !== uniqueBackgroundImages.length;

        if (galleryChanged || backgroundChanged) {
            galleryImages.length = 0;
            galleryImages.push(...uniqueGalleryImages);

            backgroundImages.length = 0;
            backgroundImages.push(...uniqueBackgroundImages);

            await saveGallery();
            await saveBackgrounds();

            renderGallery();
            renderBackgrounds();

            const removedGallery = originalGalleryCount - galleryImages.length;
            const removedBackground = originalBackgroundCount - backgroundImages.length;

            logEvent(`Automatyczne czyszczenie duplikatów: Usunięto ${removedGallery} z galerii, ${removedBackground} z tła`);

            if (removedGallery > 0 || removedBackground > 0) {
                showNotification(`Automatycznie usunięto duplikaty: ${removedGallery} z galerii, ${removedBackground} z tła`, 'info', 3000);
            }
        }

    } catch (error) {
        logEvent('Błąd podczas automatycznego czyszczenia duplikatów:', error);
    }
}
function removeDuplicatesWithGitHubPriority(images) {
    const grouped = {};

    images.forEach(img => {
        const key = img.name.toLowerCase().trim().replace(/\s+/g, ' ');

        if (!grouped[key]) {
            grouped[key] = [];
        }
        grouped[key].push(img);
    });

    const result = [];

    for (const key in grouped) {
        const group = grouped[key];

        const githubImage = group.find(img => img.source === 'github');

        if (githubImage) {
            result.push(githubImage);
        } else {
            const imageToKeep = group.reduce((newest, current) => {
                const newestDate = new Date(newest.dateAdded || 0);
                const currentDate = new Date(current.dateAdded || 0);
                return currentDate > newestDate ? current : newest;
            });
            result.push(imageToKeep);
        }
    }

    return result;
}
function filterUserData(data, defaultData, identifier = 'name') {
    return data.filter(item => {
        if (typeof defaultData[0] === 'string') {
            return !defaultData.includes(item);
        } else {
            return !defaultData.some(defaultItem => defaultItem[identifier] === item[identifier]);
        }
    });
}
