}
async function loadSystemFonts(force = false) {
    const fontSelector = document.getElementById('fontSelector');
    if (!fontSelector) return;
    const fontPermission = localStorage.getItem('fontPermission');
    const permissionGranted = fontPermission === 'granted';
    const permissionDenied = fontPermission === 'denied';
    if (permissionGranted && !systemFontsLoaded && !force) {
        showFontPermissionDialog(fontSelector);
        return;
    }
    if (!permissionGranted && !permissionDenied && !force) {
        showFontPermissionDialog(fontSelector);
        return;
    }
    if (permissionGranted || force) {
        try {
            if ('queryLocalFonts' in window) {
                const availableFonts = await window.queryLocalFonts();
                const fontFamilies = new Set();

                fontFamilies.add('Systemowa domyślna');

                availableFonts.forEach(font => {
                    fontFamilies.add(font.family);
                });

                fontSelector.innerHTML = '';
                fontFamilies.forEach(font => {
                    const option = document.createElement('option');
                    option.value = font === 'Systemowa domyślna' ? '' : font;
                    option.textContent = font;
                    fontSelector.appendChild(option);
                });

                restoreSavedFont(fontSelector);
                systemFontsLoaded = true;
                return;
            }
        } catch (error) {
            logEvent('Błąd pobierania czcionek systemowych:', error);
        }
    }
    addFallbackFonts(fontSelector);
    restoreSavedFont(fontSelector);
}
function showFontPermissionDialog(fontSelector) {
    if (document.getElementById('fontPermissionModal')) return;
    const modal = document.createElement('div');
    modal.id = 'fontPermissionModal';
    modal.className = 'modal';
    modal.innerHTML = `
        <div class="modal-content" style="max-width: 500px;">
            <div class="modal-header" style="text-align: center;display:block !important;">
                <h3>Dostęp do czcionek systemowych</h3>
            </div>
            <div class="modal-body"><center>
                <p>Aby wyświetlić listę czcionek dostępnych w systemie, aplikacja potrzebuje Twojej zgody na dostęp do nich.</p>
                <p>Czy chcesz załadować czcionki systemowe?</p></center>
            </div>
            <div class="modal-footer" style="display: flex; justify-content: space-between; gap: 10px;">
                <button id="fontPermissionDeny" class="btn-danger" style="flex: 1;">
                    <i class="fas fa-times"></i> Nie wyrażam zgody
                </button>
                <button id="fontPermissionAllow" class="btn-success" style="flex: 1;">
                    <i class="fas fa-check"></i> Wyrażam zgodę
                </button>
            </div>
        </div>
    `;
    document.body.appendChild(modal);
    modal.classList.add('show');


    setTimeout(() => {
        const content = modal.querySelector('.modal-content');
        const scrollBtn = modal.querySelector('.modal-scroll-to-top');
        if (content) content.scrollTop = 0;
        if (scrollBtn) scrollBtn.classList.remove('show');
    }, 100);
    document.getElementById('fontPermissionAllow').addEventListener('click', function() {
        localStorage.setItem('fontPermission', 'granted');
        modal.classList.remove('show');
        setTimeout(() => modal.remove(), 300);
        loadSystemFonts(true);
    });
    document.getElementById('fontPermissionDeny').addEventListener('click', function() {
        localStorage.setItem('fontPermission', 'denied');
        modal.classList.remove('show');
        setTimeout(() => modal.remove(), 300);
        addFallbackFonts(fontSelector);
        restoreSavedFont(fontSelector);
    });
}
function addFallbackFonts(fontSelector) {
    const fallbackFonts = [
        'Arial, sans-serif', 'Helvetica, sans-serif', 'Georgia, serif',
        'Times New Roman, serif', 'Courier New, monospace', 'Verdana, sans-serif',
