async function loadGallery() {
    try {
        const db = await openDB();
        const tx = db.transaction('galleryImages', 'readonly');
        const store = tx.objectStore('galleryImages');
        const request = store.getAll();
        return new Promise((resolve, reject) => {
            request.onsuccess = async function() {
                const savedImages = request.result || [];
                galleryImages = [];

                for (const savedImg of savedImages) {
                    if (savedImg.source === 'github') {
                        galleryImages.push({
                            id: savedImg.id,
                            name: savedImg.name,
                            url: savedImg.url,
                            dateAdded: savedImg.dateAdded,
                            source: 'github'
                        });
                    } else {
                        galleryImages.push({
                            id: savedImg.id,
                            name: savedImg.name,
                            data: savedImg.data,
                            dateAdded: savedImg.dateAdded,
                            source: 'local'
                        });
                    }
                }

                await cleanupGalleryDuplicatesAutomatically();
                resolve();
            };
            request.onerror = () => reject('Błąd ładowania galerii');
        });
    } catch (error) {
        logEvent(error);
        galleryImages = [];
    } finally {
        updateGalleryCount();
    }
}

async function saveGallery() {
    try {
        const db = await openDB();
        const tx = db.transaction('galleryImages', 'readwrite');
        const store = tx.objectStore('galleryImages');
        await store.clear();

        for (const img of galleryImages) {
            if (!img.id) {
                img.id = generateUniqueId();
            }

            const imageToSave = {
                id: img.id,
                name: img.name,
                dateAdded: img.dateAdded,
                source: img.source || 'local'
            };

            if (img.source === 'github') {
                imageToSave.url = img.url;
            } else {
                imageToSave.data = img.data;
            }

            await store.put(imageToSave);
        }
        return new Promise(resolve => tx.oncomplete = resolve);
    } catch (error) {
        logEvent('Błąd zapisu galerii:', error);
    }
}
const soundManager = {
  lastPlayed: {},
  minInterval: 3000,
  queue: [],
  isPlaying: false,
  play(type) {
    if (!soundPermissionGiven || !soundAllowed) return;
    const now = Date.now();
    if (this.lastPlayed[type] && now - this.lastPlayed[type] < this.minInterval) {
      return;
    }
    this.lastPlayed[type] = now;
    this.queue.push(type);
    this.processQueue();
  },
  async processQueue() {
    if (this.isPlaying || this.queue.length === 0) return;
    this.isPlaying = true;
    const type = this.queue.shift();
    try {
      const audio = new Audio(SOUND_URLS[type]);
      await audio.play();
      await new Promise(resolve => {
        setTimeout(resolve, this.minInterval);
      });
    } catch (error) {
    }
    this.isPlaying = false;
    this.processQueue();
  }
};
function playNotificationSound(type) {
  soundManager.play(type);
}
function playSound(type) {
  soundManager.play(type);
}
function playDemoSound(type) {
  soundManager.play(type);
}
function unlockAudioContext() {
  if (audioContextUnlocked) return;
  const context = new (window.AudioContext || window.webkitAudioContext)();
  const buffer = context.createBuffer(1, 1, 22050);
  const source = context.createBufferSource();
  source.buffer = buffer;
  source.connect(context.destination);
  source.start(0);
  source.onended = () => {
    audioContextUnlocked = true;
    context.close();
  };
}
