async function fetchCategoriesFromGitHub() {
    try {
        const response = await fetch('https://raw.githubusercontent.com/BarryV94/Magazyn-Produkt-w/main/categories.json');
        if (!response.ok) {
            throw new Error('Nie udało się pobrać kategorii');
        }
        const categoriesFromGitHub = await response.json();
        return categoriesFromGitHub;
    } catch (error) {
        logEvent('Błąd pobierania kategorii:', error);
        return null;
    }
}
async function updateCategoriesFromGitHub() {
    const loader = new PokeballLoader('pokeballLoader').start(0);
    loader.update(30);

    try {
        const githubCategories = await fetchCategoriesFromGitHub();
        if (githubCategories && githubCategories.length > 0) {
            const existingCategoryNames = new Set(categories.map(cat => cat.name));
            const newCategories = githubCategories.filter(cat => !existingCategoryNames.has(cat.name));

            if (newCategories.length > 0) {
                categories.push(...newCategories);
                saveCategories();
                renderCategoriesList();
                loader.complete();
                setTimeout(() => loader.reset(), 500);
                showNotification(`Dodano ${newCategories.length} nowych kategorii`, 'success');
                playNotificationSound('success');
            } else {
                loader.complete();
                setTimeout(() => loader.reset(), 500);
            }
        } else {
            throw new Error('Nie udało się pobrać kategorii');
        }
    } catch (error) {
        loader.reset();
        showNotification('Błąd podczas aktualizacji kategorii: ' + error.message, 'danger');
    }
}
function saveCategories() {
    defaultCategories.forEach(defaultCat => {
        const exists = categories.some(cat => cat.name.toLowerCase() === defaultCat.name.toLowerCase());
        if (!exists) {
            categories.push(defaultCat);
        }
    });

    localStorage.setItem('categories', JSON.stringify(categories));
    updateCategorySelects();
    renderProducts(applyAllFilters());
}
