function addCategory() {
    const newCat = document.getElementById('newCategory').value.trim();
    logEvent(`Dodawanie nowej kategorii: ${newCat}`);
    if (!newCat) return;
    if (newCat.length > 50) {
        showNotification('Nazwa kategorii zbyt długa', 'warning');
        return;
    }
    if (isProtectedCategory(newCat)) {
        showNotification('Nie można dodać kategorii o nazwie "' + newCat + '"', 'warning');
        playNotificationSound('alert');
        return;
    }
    const exists = categories.some(cat => cat.name.toLowerCase() === newCat.toLowerCase());
    if (exists) {
        showNotification('Kategoria już istnieje', 'warning');
        playNotificationSound('alert');
        return;
    }

    categories.push({
        name: newCat,
        bg: '#ededed',
        text: '#333'
    });
    saveCategories();
    renderCategoriesList();
    document.getElementById('newCategory').value = '';
    logEvent(`Dodano kategorię: ${newCat}`);
    showNotification(`Dodano kategorię: ${newCat}`, 'success');
    playNotificationSound('success');
}
function removeCategory(index) {
    const catName = categories[index].name;
    logEvent(`Próba usunięcia kategorii: ${catName}`);

    if (isProtectedCategory(catName)) {
        showNotification('Nie można usunąć domyślnej kategorii "' + catName + '"', 'warning');
        playNotificationSound('alert');
        return;
    }

    const isUsed = products.some(p => p.category === catName);
    if (isUsed) {
        showNotification('Nie można usunąć kategorii, która jest używana', 'warning');
        playNotificationSound('alert');
        return;
    }

    showConfirmation(
        'Potwierdzenie usunięcia',
        `Czy na pewno chcesz usunąć kategorię "${catName}"?`,
        function(confirmed, idx) {
            if (confirmed) {
                categories.splice(idx, 1);
                saveCategories();
                renderCategoriesList();
                logEvent(`Usunięto kategorię: ${catName}`);
                showNotification('Kategoria została usunięta', 'success');
                playNotificationSound('success');
            }
        },
        index
    );
}
function moveCategoryUp(index) {
    logEvent(`Przenoszenie kategorii w górę: ${categories[index].name}`);
    if (index <= 0) return;
    const current = categories[index];
    const above = categories[index - 1];
    if (isProtectedCategory(current.name) || isProtectedCategory(above.name)) return;
    const temp = categories[index - 1];
    categories[index - 1] = categories[index];
    categories[index] = temp;
    saveCategories();
    renderCategoriesList();
}
function moveCategoryDown(index) {
    logEvent(`Przenoszenie kategorii w dół: ${categories[index].name}`);
    if (index >= categories.length - 1) return;
    const current = categories[index];
    const below = categories[index + 1];
    if (isProtectedCategory(current.name) || isProtectedCategory(below.name)) return;
    const temp = categories[index + 1];
    categories[index + 1] = categories[index];
    categories[index] = temp;
    saveCategories();
    renderCategoriesList();
}
function updateCategoryColor(index, type, value) {
    const catName = categories[index].name;
    logEvent(`Zmiana koloru kategorii ${catName}: ${type} na ${value}`);
	const current = categories[index];
	const originalBg = current.bg;
	const originalText = current.text;
	if (type === 'bg') {
		current.bg = value;
	} else {
		current.text = value;
	}
	const isDuplicate = categories.some((cat, i) =>
		i !== index &&
		cat.bg.toLowerCase() === current.bg.toLowerCase() &&
		cat.text.toLowerCase() === current.text.toLowerCase()
	);
	if (isDuplicate) {
		showInfoModal("Inna kategoria już ma taką samą kombinację kolorów tła i tekstu!");
		current.bg = originalBg;
		current.text = originalText;
		const inputs = document.querySelectorAll(`input[type="color"]`);
		const bgInput = inputs[index * 2];
		const textInput = inputs[index * 2 + 1];
		bgInput.value = originalBg;
		textInput.value = originalText;
		bgInput.parentElement.style.backgroundColor = originalBg;
		textInput.parentElement.style.backgroundColor = originalText;
		return;
	}
	saveCategories();
	renderCategoriesList();
	renderProducts(applyAllFilters());
}
