function cleanupOldLogs() {
    const maxPages = 200;
    const maxLogs = maxPages * logsPerPage;
    if (eventLogs.length > maxLogs) {
        eventLogs = eventLogs.slice(-maxLogs);
        saveLogs();
        logEvent(`Automatyczne czyszczenie logów: zachowano ${maxLogs} najnowszych wpisów (${maxPages} stron)`);
    }
    const now = new Date();
    const retentionDays = 3;
    const filteredLogs = eventLogs.filter(log => {
        const logDate = new Date(log.timestamp);
        const diffDays = Math.floor((now - logDate) / (1000 * 60 * 60 * 24));
        return diffDays <= retentionDays;
    });
    if (filteredLogs.length !== eventLogs.length) {
        eventLogs = filteredLogs;
        saveLogs();
        logEvent(`Automatyczne czyszczenie logów: usunięto stare wpisy powyżej ${retentionDays} dni`);
    }
}
function renderCategoriesList() {
  const list = document.getElementById('categoriesList');
  list.innerHTML = '';

  const startIndex = (categoriesCurrentPage - 1) * paginationConfig.categoriesPerPage;
  const endIndex = Math.min(startIndex + paginationConfig.categoriesPerPage, categories.length);
  const pageCategories = categories.slice(startIndex, endIndex);

  if (pageCategories.length === 0) {
    list.innerHTML = '<p class="empty-state">Brak kategorii</p>';
    renderCategoriesPagination();
    return;
  }

  pageCategories.forEach((cat, index) => {
    const globalIndex = startIndex + index;
    const isEditing = globalIndex === editingCategoryIndex;
    const item = document.createElement('div');
    item.className = 'category-item';
    const isProtected = isProtectedCategory(cat.name);

    if (isEditing) {
      item.innerHTML = `
        <div style="display: flex; flex-direction: column; width: 100%;">
          <input type="text" class="edit-category-name" value="${cat.name}" placeholder="Nowa nazwa kategorii">
          <div class="category-edit-form">
            <button class="btn-success" onclick="saveCategoryEdit()">
              <i class="fas fa-save"></i> Zapisz
            </button>
            <button class="btn-danger" onclick="cancelCategoryEdit()">
              <i class="fas fa-times"></i> Anuluj
            </button>
          </div>
        </div>
      `;
    } else {
      const upDisabled = (globalIndex <= 0) || isProtected || (globalIndex > 0 && isProtectedCategory(categories[globalIndex - 1].name));
      const downDisabled = (globalIndex >= categories.length - 1) || isProtected || (globalIndex < categories.length - 1 && isProtectedCategory(categories[globalIndex + 1].name));

      item.innerHTML = `
        <div style="display: flex; justify-content: space-between; align-items: center; width: 100%;">
          <span>${cat.name}</span>
          <div class="actions">
            <label class="color-picker" style="background-color: ${cat.bg};">
              <input type="color" value="${cat.bg}" title="Kolor tła" onchange="updateCategoryColor(${globalIndex}, 'bg', this.value); this.parentElement.style.backgroundColor = this.value;">
            </label>
            <label class="color-picker" style="background-color: ${cat.text};">
              <input type="color" value="${cat.text}" title="Kolor tekstu" onchange="updateCategoryColor(${globalIndex}, 'text', this.value); this.parentElement.style.backgroundColor = this.value;">
            </label>

            <button class="btn-info ${upDisabled ? 'disabled' : ''}"
              onclick="${!upDisabled ? `moveCategoryUp(${globalIndex})` : 'return false;'}"
              ${upDisabled ? 'disabled' : ''}>
              <i class="fas fa-arrow-up"></i>
            </button>

            <button class="btn-info ${downDisabled ? 'disabled' : ''}"
              onclick="${!downDisabled ? `moveCategoryDown(${globalIndex})` : 'return false;'}"
              ${downDisabled ? 'disabled' : ''}>
              <i class="fas fa-arrow-down"></i>
            </button>

            ${!isProtected ? `
              <button class="btn-info edit-icon-btn" onclick="startCategoryEdit(${globalIndex})" title="Edytuj nazwę">
                <i class="fas fa-edit"></i>
              </button>
            ` : ''}

            <button class="btn-danger ${isProtected ? 'disabled' : ''}"
                    onclick="${!isProtected ? `removeCategory(${globalIndex})` : 'return false;'}"
                    ${isProtected ? 'disabled' : ''}>
              <i class="fas fa-trash"></i>
            </button>
          </div>
        </div>
      `;
    }

    list.appendChild(item);
  });

  renderCategoriesPagination();
}
function renderCategoriesPagination() {
  const paginationContainer = document.getElementById('categoriesPagination');
  if (!paginationContainer) return;
  paginationContainer.innerHTML = '';
  const totalPages = Math.ceil(categories.length / paginationConfig.categoriesPerPage);
  const prevBtn = document.createElement('button');
  prevBtn.innerHTML = '<i class="fas fa-chevron-left" style="color: white;"></i>';
  prevBtn.disabled = categoriesCurrentPage === 1;
  prevBtn.addEventListener('click', () => {
    if (categoriesCurrentPage > 1) {
      categoriesCurrentPage--;
      renderCategoriesList();
    }
  });
  const nextBtn = document.createElement('button');
  nextBtn.innerHTML = '<i class="fas fa-chevron-right" style="color: white;"></i>';
  nextBtn.disabled = categoriesCurrentPage === totalPages;
  nextBtn.addEventListener('click', () => {
    if (categoriesCurrentPage < totalPages) {
      categoriesCurrentPage++;
      renderCategoriesList();
    }
  });
  const pageInfo = document.createElement('span');
  pageInfo.textContent = `Strona ${categoriesCurrentPage} z ${totalPages}`;
  paginationContainer.appendChild(prevBtn);
  paginationContainer.appendChild(pageInfo);
  paginationContainer.appendChild(nextBtn);
}
