function detectBrowserAndSetLimits() {
  const userAgent = navigator.userAgent;
  let browserName = "Nieznana przeglądarka";
  let indexedDBMax = 10;
  let indexedDBNote = "";
  let localStorageMax = 5;

  if (/Chrome|Chromium|Edg|OPR/.test(userAgent)) {
    browserName = "Chromium (Chrome, Edge, Opera, Brave, Vivaldi)";
    indexedDBMax = 0;
    indexedDBNote = "IndexedDB: do 60% miejsca na dysku";
  } else if (/Firefox/.test(userAgent)) {
    browserName = "Firefox";
    indexedDBMax = 10240;
    indexedDBNote = "IndexedDB: do 10 GB w trybie best-effort";
  } else if (/Safari/.test(userAgent) && !/Chrome/.test(userAgent)) {
    browserName = "Apple Safari";
    indexedDBMax = navigator.maxTouchPoints ? 50 : 500;
    indexedDBNote = navigator.maxTouchPoints ? 
      "IndexedDB: do 50 MB (iOS)" : "IndexedDB: do 500 MB (desktop)";
  } else if (/Trident|MSIE/.test(userAgent)) {
    browserName = "Internet Explorer";
    indexedDBMax = 250;
    indexedDBNote = "IndexedDB: do 250 MB (możliwość zwiększenia do 1 GB)";
  } else {
    browserName = "Inna przeglądarka";
    indexedDBMax = 5;
    indexedDBNote = "IndexedDB: domyślnie do 5 MB";
  }
  const setTextIfExists = (id, text) => {
    const element = document.getElementById(id);
    if (element) {
      element.textContent = text;
    } else {
      console.warn(`Element o ID '${id}' nie istnieje`);
    }
  };

  setTextIfExists('browserInfo', browserName);
  setTextIfExists('localStorageMax', localStorageMax + ' MB');

  if (indexedDBMax === 0) {
    setTextIfExists('indexedDBMax', "60% dysku");
    setTextIfExists('indexedDBAvailable', "Dynamiczne");
  } else {
    setTextIfExists('indexedDBMax', indexedDBMax + ' MB');
  }

  setTextIfExists('indexedDBNote', indexedDBNote);
}
async function handleFileImport(filePath) {
    logEvent('Import pliku z Androida: ' + filePath);

    const loader = new PokeballLoader('pokeballLoader').start(0);
    loader.update(10);
    try {
        const fileUrl = filePath.startsWith('https://app.local/') ? filePath : ('file://' + filePath);
        const response = await fetch(fileUrl);
        if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`);
        }
        const blob = await response.blob();
        loader.update(30);
        const file = new File([blob], 'imported_file.zip', { type: 'application/zip' });
        await processImportFile(file);
    } catch (fetchError) {
        try {
            const file = await new Promise((resolve, reject) => {
                const xhr = new XMLHttpRequest();
                xhr.open('GET', 'file://' + filePath, true);
                xhr.responseType = 'blob';

                xhr.onload = function() {
                    if (xhr.status === 200) {
                        const blob = xhr.response;
                        const file = new File([blob], 'imported_file.zip', { type: 'application/zip' });
                        resolve(file);
                    } else {
                        reject(new Error(`XHR error! status: ${xhr.status}`));
                    }
                };

                xhr.onerror = function() {
                    reject(new Error('XHR network error'));
                };
                xhr.send();
            });
            loader.update(30);
            await processImportFile(file);
        } catch (xhrError) {
            loader.reset();
            showNotification('Błąd: Nie można odczytać pliku. Spróbuj ponownie wybrać plik ręcznie.', 'danger');
            setTimeout(() => {
                importData();
            }, 2000);
        }
    }
}
async function importDataFromAndroid(filePath) {
    return await handleFileImport(filePath);
}
window.addEventListener('import-file', function(event) {
    if (event.detail && event.detail.filePath) {
        handleFileImport(event.detail.filePath);
    }
});
window.addEventListener('file-import', function(event) {
    if (event.detail && event.detail.filePath) {
        handleFileImport(event.detail.filePath);
    }
});
async function calculateMemoryUsage() {
  const loader = new PokeballLoader('pokeballLoader').start(0);
  loader.update(10);
  try {
    const userAgent = navigator.userAgent;
    let browserName = "Nieznana przeglądarka";
    if (/Chrome|Chromium/.test(userAgent)) browserName = "Google Chrome";
    else if (/Firefox/.test(userAgent)) browserName = "Mozilla Firefox";
    else if (/Safari/.test(userAgent)) browserName = "Apple Safari";
    else if (/Edg/.test(userAgent)) browserName = "Microsoft Edge";
    else if (/OPR/.test(userAgent)) browserName = "Opera";
    document.getElementById('browserInfo').textContent = browserName;

    const localStorageDetails = {
      products: JSON.parse(localStorage.getItem('products') || '[]'),
      categories: JSON.parse(localStorage.getItem('categories') || '[]'),
      exchangeRates: JSON.parse(localStorage.getItem('exchangeRates') || '{}'),
      currencySymbols: JSON.parse(localStorage.getItem('currencySymbols') || '{}'),
      shops: JSON.parse(localStorage.getItem('shops') || '[]'),
      effects: JSON.parse(localStorage.getItem('effects') || '[]'),
      eventLogs: JSON.parse(localStorage.getItem('eventLogs') || '[]')
    };

    let localStorageSize = 0;
    for (let i = 0; i < localStorage.length; i++) {
      const key = localStorage.key(i);
      const value = localStorage.getItem(key);
      localStorageSize += (key.length + value.length) * 2;
    }

    const localStorageMB = localStorageSize / (1024 * 1024);
    const localStorageMax = 5;
    const localStoragePercent = (localStorageMB / localStorageMax) * 100;

    const setTextIfExists = (elementId, text) => {
      const element = document.getElementById(elementId);
      if (element) element.textContent = text;
    };

    setTextIfExists('localStorageProducts', localStorageDetails.products.length);
    setTextIfExists('localStorageCategories', localStorageDetails.categories.length);
    setTextIfExists('localStorageSettings', 
      Object.keys(localStorageDetails.exchangeRates).length + 
      Object.keys(localStorageDetails.currencySymbols).length);
    setTextIfExists('localStorageShops', localStorageDetails.shops.length);
    setTextIfExists('localStorageEffects', localStorageDetails.effects.length);
    setTextIfExists('localStorageLogs', localStorageDetails.eventLogs.length);

    const db = await openDB();
    const stores = ['productImages', 'galleryImages', 'backgroundImages'];
    let productImagesCount = 0;
    let galleryImagesCount = 0;
    let backgroundImagesCount = 0;
    let indexedDBImagesSize = 0;

    for (const storeName of stores) {
      const tx = db.transaction(storeName, 'readonly');
      const store = tx.objectStore(storeName);
      const request = store.getAll();
      const data = await new Promise(resolve => {
        request.onsuccess = () => resolve(request.result);
      });

      if (storeName === 'productImages') {
        productImagesCount = data.length;
      } else if (storeName === 'galleryImages') {
        galleryImagesCount = data.length;
      } else if (storeName === 'backgroundImages') {
        backgroundImagesCount = data.length;
      }

      for (const item of data) {
        if (item.source === 'github') continue;

        if (item.data && typeof item.data === 'string') {
          try {
            const base64Data = item.data.split(',')[1] || '';
            const binarySize = (base64Data.length * 3) / 4;
            indexedDBImagesSize += binarySize;
          } catch (error) {
            logEvent('Błąd przetwarzania danych obrazu:', error);
          }
        }
      }
    }

    const indexedDBImagesMB = indexedDBImagesSize / (1024 * 1024);
    let indexedDBMaxMB = 10;
    if (browserName.includes('Chrome') || browserName.includes('Edge') || browserName.includes('Opera')) {
      try {
        const estimate = await navigator.storage.estimate();
        indexedDBMaxMB = (estimate.quota / (1024 * 1024)) * 0.6;
      } catch (e) {
        logEvent("Błąd podczas szacowania przestrzeni dyskowej:", e);
        indexedDBMaxMB = 10240;
      }
    }

    setTextIfExists('dbProductImages', productImagesCount);
    setTextIfExists('dbGalleryImages', galleryImagesCount);
    setTextIfExists('dbImagesSize', indexedDBImagesMB.toFixed(2) + ' MB');
    setTextIfExists('dbOtherData', '0.00 MB');

    const localStorageBar = document.getElementById('localStorageBar');
    const localStorageText = document.getElementById('localStorageText');
    if (localStorageBar && localStorageText) {
      localStorageBar.style.width = `${Math.min(localStoragePercent, 100)}%`;
      localStorageText.textContent = `${localStorageMB.toFixed(2)} MB / ${localStorageMax} MB`;
    }

    const indexedDBBar = document.getElementById('indexedDBBar');
    const indexedDBText = document.getElementById('indexedDBText');

    setTextIfExists('localStorageAvailable', (localStorageMax - localStorageMB).toFixed(2) + ' MB');
    setTextIfExists('indexedDBAvailable', (indexedDBMaxMB - indexedDBImagesMB).toFixed(2) + ' MB');
    setTextIfExists('indexedDBMax', indexedDBMaxMB.toFixed(2) + ' MB');

    const totalUsage = localStorageMB + indexedDBImagesMB;
    const totalAvailable = (localStorageMax + indexedDBMaxMB - totalUsage).toFixed(2) + ' MB';
    const totalPercentage = (totalUsage / (localStorageMax + indexedDBMaxMB)) * 100;

    setTextIfExists('totalUsage', totalUsage.toFixed(2) + ' MB');
    setTextIfExists('availableSpace', totalAvailable);
    setTextIfExists('totalPercentage', totalPercentage.toFixed(1) + '%');

    if (indexedDBBar && indexedDBText) {
      const indexedDBPercent = (indexedDBImagesMB / indexedDBMaxMB) * 100;
      indexedDBBar.style.width = `${Math.min(indexedDBPercent, 100)}%`;
      indexedDBText.textContent = `${indexedDBImagesMB.toFixed(2)} MB / ${indexedDBMaxMB.toFixed(2)} MB`;
    }

  } catch (error) {
    logEvent('Błąd obliczania pamięci:', error);
    showNotification('Błąd podczas obliczania statystyk pamięci: ' + error.message, 'danger');
  } finally {
    loader.reset();
  }
}
