async function fetchExchangeRates() {
    logEvent('Rozpoczęcie aktualizacji kursów walut');
    const loader = new PokeballLoader('pokeballLoader').start(0);
    loader.update(20);
    try {
        const goldRate = exchangeRates.GOLD;
        const response = await fetch('https://api.nbp.pl/api/exchangerates/tables/a/?format=json');
        if (!response.ok) {
            throw new Error('Błąd pobierania kursów walut');
        }
        const data = await response.json();
        const rates = data[0].rates;
        const oldRates = {...exchangeRates};
        rates.forEach(rate => {
            exchangeRates[rate.code] = rate.mid;
            if (!currencySymbols[rate.code]) {
                currencySymbols[rate.code] = rate.code;
            }
        });
        exchangeRates.PLN = 1.0;
        currencySymbols.PLN = 'zł';

        if (goldRate) {
            exchangeRates.GOLD = goldRate;
        }
        exchangeRates.lastUpdated = new Date().toISOString();
        saveExchangeRates();
        loader.update(80);
        let ratesChanged = false;
        for (const currency in exchangeRates) {
            if (currency !== 'lastUpdated' && currency !== 'GOLD' && oldRates[currency] !== exchangeRates[currency]) {
                ratesChanged = true;
                break;
            }
        }
        if (ratesChanged) {
            setTimeout(async () => {
                await checkAndUpdatePriceHistoryOnRateChange();
