function getCurrencySymbol(currency) {
	return currencySymbols[currency] || currency;
}
function formatCurrency(value, originalCurrency, showSymbol = true, section = 'default') {
    if (value === null || value === undefined || isNaN(value)) return 'Brak';
    let targetCurrency = originalCurrency;
    switch(section) {
        case 'extendedStats':
            targetCurrency = currencyConfig.extendedStatsCurrency;
            break;
        case 'mainStats':
            targetCurrency = currencyConfig.mainStatsCurrency;
            break;
        case 'priceDiff':
            targetCurrency = currencyConfig.priceDiffCurrency;
            break;
        case 'priceHistory':
            targetCurrency = currencyConfig.priceHistoryCurrency;
            break;
        case 'otherLists':
            targetCurrency = currencyConfig.otherListsCurrency;
            break;
        case 'purchasePrice':
            targetCurrency = currencyConfig.purchasePriceCurrency || 'PLN';
            break;
        case 'currentPrice':
            targetCurrency = currencyConfig.currentPriceCurrency || 'PLN';
            break;
        case 'goldBuyback':
            targetCurrency = currencyConfig.goldBuybackCurrency || 'PLN';
            break;
    }
    let displayValue = value;
    if (originalCurrency !== targetCurrency) {
        try {
            displayValue = convertToCurrency(value, originalCurrency, targetCurrency);
        } catch (error) {
            logEvent('Błąd konwersji waluty:', error);
            displayValue = value;
            targetCurrency = originalCurrency;
        }
    }
    const formattedValue = parseFloat(displayValue).toFixed(2).replace('.', ',');
    if (showSymbol) {
        return `${formattedValue} ${getCurrencySymbol(targetCurrency)}`;
    }
    return formattedValue;
}
