function convertToCurrency(amount, fromCurrency, toCurrency) {
  if (fromCurrency === toCurrency) return amount;
  if (fromCurrency !== 'PLN' && (!exchangeRates[fromCurrency] || isNaN(exchangeRates[fromCurrency]))) {
    logEvent(`Nieznana lub nieprawidłowa waluta źródłowa: ${fromCurrency}`);
    return amount;
  }
  if (toCurrency !== 'PLN' && (!exchangeRates[toCurrency] || isNaN(exchangeRates[toCurrency]))) {
    logEvent(`Nieznana lub nieprawidłowa waluta docelowa: ${toCurrency}`);
    return amount;
  }
  let amountInPLN = amount;
  if (fromCurrency !== 'PLN') {
    amountInPLN = amount * exchangeRates[fromCurrency];
  }
  if (toCurrency === 'PLN') return amountInPLN;
  return amountInPLN / exchangeRates[toCurrency];
}
