function showNotification(message, type = 'info', duration = 3000) {
    if (!showNotifications) return;
    logEvent(`Powiadomienie [${type}]: ${message}`);
	const notification = {
		message,
		type,
		duration
	};
	notificationQueue.push(notification);
	if (!isNotificationVisible) {
		processNotificationQueue();
	}
}
function processNotificationQueue() {
	if (notificationQueue.length === 0) {
		isNotificationVisible = false;
		return;
	}
	isNotificationVisible = true;
	const notification = notificationQueue.shift();
	createNotificationElement(notification);
}
function createNotificationElement(notification) {
  const container = document.getElementById('notificationContainer');
  if (!container) {
    logEvent('Notification container not found!');
    return;
  }
  const notificationEl = document.createElement('div');
  notificationEl.className = `notification ${notification.type} show`;
	let icon = '';
	switch(notification.type) {
		case 'success': icon = 'fa-check-circle'; break;
		case 'warning': icon = 'fa-exclamation-triangle'; break;
		case 'danger': icon = 'fa-times-circle'; break;
		default: icon = 'fa-info-circle';
	}
	notificationEl.innerHTML = `
		<i class="fas ${icon}"></i>
		<span>${notification.message}</span>
		<button class="close-notification">
			<i class="fas fa-times"></i>
		</button>
		<div class="notification-progress"></div>
	`;
	const progressBar = notificationEl.querySelector('.notification-progress');
	progressBar.style.animationDuration = `${notification.duration}ms`;
	const closeBtn = notificationEl.querySelector('.close-notification');
	closeBtn.addEventListener('click', () => {
	notificationEl.classList.remove('show');
	setTimeout(() => {
		if (container.contains(notificationEl)) {
		container.removeChild(notificationEl);
		}
		processNotificationQueue();
	}, 300);
	});
	container.appendChild(notificationEl);
	setTimeout(() => {
	notificationEl.classList.remove('show');
	setTimeout(() => {
		if (container.contains(notificationEl)) {
		container.removeChild(notificationEl);
		}
		processNotificationQueue();
	}, 300);
	}, notification.duration);
}
