async function loadImageFromDB(imageId, storeName) {
    return new Promise(async (resolve, reject) => {
        try {
            const db = await openDB();
            const tx = db.transaction(storeName, 'readonly');
            const store = tx.objectStore(storeName);
            const request = store.get(imageId);
            request.onsuccess = (e) => {
                if (e.target.result) {
                    resolve(e.target.result.data);
                } else {
                    reject(`Obraz o ID ${imageId} nie znaleziony`);
                }
            };
            request.onerror = (e) => {
                reject(e.target.error || 'Unknown error');
            };
        } catch (error) {
            reject(error);
        }
    });
}
