function updateSummary() {
    let purchaseValueTotalPLN = 0;
    let currentValueTotalPLN = 0;
    let profitPLN = 0;
    let lossPLN = 0;
    let withPriceCount = 0;
    products.forEach(p => {
        if (p.category === 'Złoto') {
            const purchasePLN = convertToPLN(p.price, p.currency);
            const totalPurchase = purchasePLN * p.quantity;
            purchaseValueTotalPLN += totalPurchase;

            let currentPLN = 0;
            if (p.currentPrice !== null && p.currentPrice !== undefined) {
                currentPLN = convertToPLN(p.currentPrice, p.currentCurrency || p.currency) * p.quantity;
                withPriceCount++;
            } else {
                currentPLN = totalPurchase;
            }
            currentValueTotalPLN += currentPLN;

            const diff = currentPLN - totalPurchase;
            if (diff > 0) {
                profitPLN += diff;
            } else if (diff < 0) {
                lossPLN += Math.abs(diff);
            }
        } else {
            const purchasePLN = convertToPLN(p.price, p.currency);
            const totalPurchase = purchasePLN * p.quantity;
            purchaseValueTotalPLN += totalPurchase;

            let currentPLN = totalPurchase;
            if (p.currentPrice !== null && p.currentPrice !== undefined) {
                currentPLN = convertToPLN(p.currentPrice, p.currentCurrency || p.currency) * p.quantity;
                withPriceCount++;
            }
            currentValueTotalPLN += currentPLN;

            const diff = currentPLN - totalPurchase;
            if (diff > 0) {
                profitPLN += diff;
            } else if (diff < 0) {
                lossPLN += Math.abs(diff);
            }
        }
    });

    const profitPercent = purchaseValueTotalPLN > 0 ? (profitPLN / purchaseValueTotalPLN) * 100 : 0;
    const lossPercent = purchaseValueTotalPLN > 0 ? (lossPLN / purchaseValueTotalPLN) * 100 : 0;
    const diff = currentValueTotalPLN - purchaseValueTotalPLN;

    totalValue.textContent = formatCurrency(currentValueTotalPLN, 'PLN', true, 'mainStats');
    purchaseValue.textContent = formatCurrency(purchaseValueTotalPLN, 'PLN', true, 'mainStats');
    currentValue.textContent = formatCurrency(currentValueTotalPLN, 'PLN', true, 'mainStats');
    valueDifference.textContent = formatCurrency(diff, 'PLN', true, 'mainStats');
    valueDifference.className = diff > 0 ? 'profit' : (diff < 0 ? 'loss' : '');
    totalProfit.textContent = formatCurrency(profitPLN, 'PLN', true, 'mainStats');
    totalLoss.textContent = formatCurrency(lossPLN, 'PLN', true, 'mainStats');
    profitPercentage.textContent = profitPercent.toFixed(2).replace('.', ',') + '%';
    profitPercentage.className = profitPLN > 0 ? 'profit' : '';
    lossPercentage.textContent = lossPercent.toFixed(2).replace('.', ',') + '%';
    lossPercentage.className = lossPLN > 0 ? 'loss' : '';
    productsWithPrice.textContent = withPriceCount;
    totalProducts.textContent = products.length;
}
