          <div class="buttons" style="margin-top: 0.5rem;">
            <button class="btn-success" onclick="saveInline('${p.id}')"><i class="fas fa-save"></i> Zapisz</button>
            <button class="btn-danger" onclick="cancelInline('${p.id}')"><i class="fas fa-times"></i> Anuluj</button>
          </div>
          <div style="margin-top: 0.5rem; font-size: 0.8rem; color: #666;">
            * Pole wymagane
          </div>
        </div>
      </div>
    `;

    const editCat = productCard.querySelector(`#editCategory-${index}`);
    if (editCat) {
      editCat.addEventListener('change', function() {
        updateEditFormFields(this.value, productCard, index);
      });
    }

  } else {
    const categoryObj = categories.find(cat => cat.name.toLowerCase() === (p.category || '').toLowerCase()) || { bg: '#ededed', text: '#333' };

    let warningIcon = '';
    if (p.currentPrice != null && isPriceOutdated(p)) {
      warningIcon = `
        <span class="price-warning" title="Należy zaktualizować aktualną cenę">
          <i class="fas fa-exclamation-triangle blink-icon"></i>
        </span>
      `;
    }

    let goldStar = '';
    if (p.currentPrice != null && p.currentPrice !== undefined) {
      try {
        const currentPLN = convertToPLN(p.currentPrice, p.currentCurrency || p.currency);
        const totalValue = currentPLN * p.quantity;
        if (totalValue > 5000) {
          goldStar = `
            <span class="gold-star" title="Wartość produktu przekroczyła kwotę 5000zł.">
              <i class="fas fa-star"></i>
            </span>
          `;
        }
      } catch (e) {
        logEvent('Błąd obliczania wartości produktu:', e);
      }
    }

	let priceTrendHtml = '';
	if (showPriceTrend && priceTrendAllow) {
	priceTrendHtml = `
		<div class="detail-item">
		<span class="detail-label">Price Trend (Cardmarket):</span>
		<span class="detail-value">
			<div class="cardmarket-trend" id="trend-${p.id}">
			<div class="loading-trend">Ładowanie...</div>
			</div>
		</span>
		</div>
	`;
	} else if (showPriceTrend && !priceTrendAllow) {
	priceTrendHtml = `
		<div class="detail-item">
		<span class="detail-label">Price Trend (Cardmarket):</span>
		<span class="detail-value">
			<div class="cardmarket-trend">
			<span class="no-data">Niedostępne</span>
			</div>
		</span>
		</div>
	`;
	}

    let priceDiffHtml = '';
    if (p.currentPrice != null && p.currentPrice !== undefined) {
      const purchasePLN = convertToPLN(p.price, p.currency);
      const currentPLN = convertToPLN(p.currentPrice, p.currentCurrency || p.currency);
      const diff = (currentPLN - purchasePLN) * p.quantity;
      const diffPercent = purchasePLN > 0 ? ((currentPLN - purchasePLN) / purchasePLN) * 100 : 0;
      const diffClass = diff > 0 ? 'positive' : (diff < 0 ? 'negative' : 'neutral');
      const diffSign = diff > 0 ? '+' : '';

      const sameCurrency = p.currency === (p.currentCurrency || p.currency);
      let profitInCurrency = '';

      if (sameCurrency) {
        const profitVal = (p.currentPrice - p.price) * p.quantity;
        profitInCurrency = formatCurrency(Math.abs(profitVal), p.currency, false, 'priceDiff');
      }

      priceDiffHtml = `
        <div class="price-difference ${diffClass}">
          ${sameCurrency ? `
            <div>${diffSign}${profitInCurrency} ${getCurrencySymbol(p.currency)}</div>
            <div>(${diffSign}${formatCurrency(Math.abs(diff), 'PLN', true, 'priceDiff')})</div>
          ` : `
            <div>${diffSign}${formatCurrency(Math.abs(diff), 'PLN', true, 'priceDiff')}</div>
          `}
          <div>${diffSign}${Math.abs(diffPercent).toFixed(2).replace('.', ',')}%</div>
        </div>
      `;
    }

    let imageHtml;
    const productImage = loadProductImage(p);
    if (productImage) {
      imageHtml = `<img src="${productImage}" alt="${p.name}">`;
    } else if (p.imageId) {
      imageHtml = `<img src="" data-image-id="${p.imageId}" alt="${p.name}">`;
    } else {
      imageHtml = `<i class="fas fa-box-open" style="font-size: 3rem; color: #b0bec5;"></i>`;
    }

    const currentPriceDisplay = p.category === 'Złoto' ?
      (p.currentPrice !== null && p.currentPrice !== undefined ?
        `${formatCurrency(p.currentPrice, 'PLN', true, 'otherLists')}/g` :
        'Brak danych') :
      (p.currentPrice !== null && p.currentPrice !== undefined ?
        formatCurrency(p.currentPrice, p.currentCurrency || p.currency, true, 'otherLists') :
        'Brak');

    const purchasePriceDisplay = formatCurrency(p.price, p.currency, true, 'otherLists') + (p.category === 'Złoto' ? '/g' : '');

    const cardmarketButton = showCardmarket ? 
      `<button class="btn-cardmarket" style="position: absolute; top: 5px; left: 10px; z-index: 2; max-width: 32%;"> 
        <i class="fas fa-search"></i> cardmarket
      </button>` : '';

    const conditionBadge = (p.category === 'Singles' && p.condition) ? 
      `<div class="card-condition" style="margin-top: 5px; font-size: 0.9em; color: #666; text-align: center; display: block; width: 100%;">Stan: ${p.condition}</div>` : '';

    const languageBadge = (!['Złoto', 'Akcesoria', 'Inne'].includes(p.category) && p.language) ? 
      `<div class="language-badge" style="position: absolute; top: 5px; right: 10px; z-index: 2; background: rgba(0,0,0,0.6); color: white; padding: 2px 6px; border-radius: 10px; font-size: 0.8em;">
        ${p.language}
      </div>` : '';

    productCard.innerHTML = `
      ${selectionMode ? `<input type="checkbox" class="product-checkbox" ${isSelected ? 'checked' : ''} onchange="toggleProductSelection(${originalIndex}, this.checked)">` : ''}
      <div class="product-image" style="position: relative;">
        ${cardmarketButton}
        ${languageBadge}
        ${goldStar}
        ${imageHtml}
      </div>
      <div class="product-content">
        <div class="category-tag" style="background:${categoryObj.bg}; color:${categoryObj.text}">${p.category}</div>
        <h3>${p.name}</h3>
        ${conditionBadge}
        <div class="product-details">
          <div class="detail-item"><span class="detail-label">${p.category === 'Złoto' ? 'Ilość (gram)' : 'Ilość'}</span><span class="detail-value">${p.quantity}</span></div>
          <div class="detail-item"><span class="detail-label">${p.category === 'Złoto' ? 'Cena zakupu za 1 gram' : 'Cena zakupu jednej sztuki'}</span><span class="detail-value">${purchasePriceDisplay}</span></div>
          <div class="detail-item"><span class="detail-label">Rok zakupu</span><span class="detail-value">${p.year}</span></div>
          <div class="detail-item"><span class="detail-label">Data dodania</span><span class="detail-value">${formatDate(p.dateAdded)}</span></div>
          <div class="detail-item">
            <span class="detail-label">${p.category === 'Złoto' ? 'Aktualna cena za 1 gram' : 'Aktualna cena za jedną sztukę'}</span>
            <span class="detail-value">
              ${currentPriceDisplay}
              ${warningIcon}
            </span>
          </div>
          ${priceTrendHtml}
        </div>
        ${priceDiffHtml}
        <div class="button-group" style="display: flex; justify-content: space-between; align-items: center; margin-top: 10px; min-height: 40px;">
          <button class="delete-btn btn-danger" onclick="deleteProduct('${p.id}')"><i class="fas fa-trash-alt"></i> Usuń</button>
          <button class="edit-btn btn-info" onclick="startInline('${p.id}')"><i class="fas fa-edit"></i> Edytuj</button>
        </div>
      </div>
    `;

    const imgContainer = productCard.querySelector('.product-image');
    if (imgContainer) {
      imgContainer.addEventListener('click', () => {
        const originalIndex = products.findIndex(prod => prod.id === p.id);
        openProductPreview(originalIndex);
      });

      if (showCardmarket) {
        const btn = imgContainer.querySelector('.btn-cardmarket');
        if (btn) {
          if (p.category === 'Złoto') {
            btn.innerHTML = '<i class="fas fa-search"></i> Mennica Polska';
            btn.addEventListener('click', e => {
              e.stopPropagation();
              window.open('https://www.mennica.com.pl/produkty-inwestycyjne/analiza-rynku-zlota', '_blank');
            });
          } else {
            btn.addEventListener('click', e => {
              e.stopPropagation();
              searchCardmarket(p.name);
            });
          }
        }
      }

      const imgEl = imgContainer.querySelector('img[data-image-id]');
      if (imgEl) {
        loadImageFromDB(p.imageId, 'productImages')
          .then(dataUrl => { imgEl.src = dataUrl; })
          .catch(() => {
            const x = document.createElement('i');
            x.className = 'fas fa-times-circle';
            x.style.cssText = 'font-size: 3rem; color: #ff6f00;';
            imgEl.replaceWith(x);
          });
      }
    }

    if (showPriceTrend) {
      setTimeout(async () => {
        try {
          const trend = await fetchCardmarketPriceTrend(p.name);
          const trendElement = document.getElementById(`trend-${p.id}`);
          if (trendElement) {
            if (trend) {
              trendElement.innerHTML = `<span class="price-trend-value">${trend}</span>`;
            } else {
              trendElement.innerHTML = '<span class="no-data">Brak danych</span>';
            }
          }
        } catch (error) {
          logEvent('Błąd pobierania Price Trend:', error);
          const trendElement = document.getElementById(`trend-${p.id}`);
          if (trendElement) {
            trendElement.innerHTML = '<span class="no-data">Błąd pobierania</span>';
          }
        }
      }, 1000);
    }
  }

  productCard.dataset.id = p.id;
  return productCard;
}
async function addDailyPricePoints() {
    const today = new Date().toISOString().split('T')[0];
    const lastUpdate = localStorage.getItem('lastPriceHistoryUpdate') || '';

    if (lastUpdate !== today) {
        const loader = new PokeballLoader('pokeballLoader').start(0);
        loader.update(10);
        try {
            const goldPrice = await fetchGoldPrice();

            loader.update(40);

            for (const product of products) {
                if (product.category === 'Złoto') {
                    if (goldPrice !== null) {
                        product.currentPrice = goldPrice;
                        product.currentCurrency = 'PLN';
                        product.lastPriceUpdate = new Date().toISOString();

                        const history = await priceHistory.getPriceHistory(product.id);
                        const lastEntry = history.length > 0 ? history[history.length - 1] : null;
                        const shouldAdd = !lastEntry || 
                            !(new Date(lastEntry.date).toISOString().split('T')[0] === today && 
                            lastEntry.price === goldPrice && 
                            lastEntry.currency === 'PLN');

                        if (shouldAdd) {
                            await priceHistory.addPricePoint(
                                product.id,
                                goldPrice,
                                'PLN'
                            );
                        }
                    }
                } else if (product.currentPrice !== null && product.currentPrice !== undefined) {
                    const history = await priceHistory.getPriceHistory(product.id);
                    const lastEntry = history.length > 0 ? history[history.length - 1] : null;
                    const shouldAdd = !lastEntry || 
                        !(new Date(lastEntry.date).toISOString().split('T')[0] === today && 
                        lastEntry.price === product.currentPrice && 
                        lastEntry.currency === (product.currentCurrency || product.currency));

                    if (shouldAdd) {
                        await priceHistory.addPricePoint(
                            product.id,
                            product.currentPrice,
                            product.currentCurrency || product.currency
                        );
                    }
                }
            }

            localStorage.setItem('lastPriceHistoryUpdate', today);
            loader.update(80);
            await saveProducts();
            loader.complete();
            setTimeout(() => loader.reset(), 500);
        } catch (error) {
            loader.reset();
            logEvent('Błąd dodawania dziennych punktów cenowych:', error);
        }
    }
}
