function renderProducts(list = applyAllFilters()) {
  productList.innerHTML = '';
  currentDisplayedProducts = list;

  const totalPages = Math.ceil(list.length / paginationConfig.productsPerPage);
  productsCurrentPage = Math.min(productsCurrentPage, totalPages);

  const startIndex = (productsCurrentPage - 1) * paginationConfig.productsPerPage;
  const endIndex = Math.min(startIndex + paginationConfig.productsPerPage, list.length);
  const currentPageProducts = list.slice(startIndex, endIndex);

  if (currentPageProducts.length === 0) {
    emptyState.style.display = 'block';
    renderProductsPagination(totalPages);
    return;
  }

  emptyState.style.display = 'none';

  currentPageProducts.forEach((p, i) => {
    const card = renderProduct(p, i, p.editing);
    productList.appendChild(card);

    const imgContainer = card.querySelector('.product-image');
    if (imgContainer) {
      imgContainer.addEventListener('click', () => {
        const originalIndex = products.findIndex(prod => prod.id === p.id);
        openProductPreview(originalIndex);
      });

      const btn = imgContainer.querySelector('.btn-cardmarket');
      if (btn) {
        if (p.category === 'Złoto') {
          btn.innerHTML = '<i class="fas fa-search"></i> Mennica Polska';
          btn.addEventListener('click', e => {
            e.stopPropagation();
            window.open('https://www.mennica.com.pl/produkty-inwestycyjne/analiza-rynku-zlota', '_blank');
          });
        } else {
          btn.addEventListener('click', e => {
            e.stopPropagation();
            searchCardmarket(p.name);
          });
        }
      }
    }
    const imgEl = card.querySelector('img[data-image-id]');
    if (imgEl) {
      loadImageFromDB(p.imageId, 'productImages')
        .then(dataUrl => { imgEl.src = dataUrl; })
        .catch(() => {
          const x = document.createElement('i');
          x.className = 'fas fa-times-circle';
          x.style.cssText = 'font-size: 3rem; color: #ff6f00;';
          imgEl.replaceWith(x);
        });
    }

    if (p.category !== 'Złoto') {
      setTimeout(() => updateProductPriceTrend(p.id, p.name), 500 * (i + 1));
    }
  });
  renderProductsPagination(totalPages);
  addPaginationInfo();
  setTimeout(initLazyLoading, 100);
}
async function updateProductPriceTrend(productId, productName, retryCount = 0) {
  if (!priceTrendAllow) {
    const trendElement = document.getElementById(`trend-${productId}`);
    if (trendElement) {
      trendElement.innerHTML = '<span class="no-data">Niedostępne</span>';
    }
    return;
  }
  const maxRetries = 2;
  const retryDelay = 5000;

  try {
    const trend = await fetchCardmarketPriceTrend(productName);
    const trendElement = document.getElementById(`trend-${productId}`);

    if (!trendElement) return;

    if (trend) {
      trendElement.innerHTML = `<span class="price-trend-value">${trend}</span>`;
    } else {
      if (retryCount < maxRetries) {
        trendElement.innerHTML = `<div class="loading-trend">Ponawianie... (${retryCount + 1}/${maxRetries})</div>`;
        setTimeout(() => {
          updateProductPriceTrend(productId, productName, retryCount + 1);
        }, retryDelay);
      } else {
        trendElement.innerHTML = '<span class="no-data">Brak danych po wielu próbach</span>';
      }
    }
  } catch (error) {
    logEvent('Błąd aktualizacji Price Trend:', error);
    const trendElement = document.getElementById(`trend-${productId}`);

    if (trendElement) {
      if (retryCount < maxRetries) {
        trendElement.innerHTML = `<div class="loading-trend">Błąd, ponawianie... (${retryCount + 1}/${maxRetries})</div>`;
        setTimeout(() => {
          updateProductPriceTrend(productId, productName, retryCount + 1);
        }, retryDelay);
      } else {
        trendElement.innerHTML = '<span class="no-data">Błąd pobierania</span>';
      }
    }
  }
}
function renderProductsPagination(totalPages) {
  let paginationContainer = document.getElementById('productsPagination');
  if (!paginationContainer) {
    paginationContainer = document.createElement('div');
    paginationContainer.id = 'productsPagination';
    paginationContainer.className = 'pagination-container';
    productList.parentNode.insertBefore(paginationContainer, productList.nextSibling);
  }
  paginationContainer.innerHTML = '';
  if (totalPages <= 1) {
    paginationContainer.style.display = 'none';
    return;
  }
  paginationContainer.style.display = 'flex';
  const prevBtn = document.createElement('button');
  prevBtn.innerHTML = '<i class="fas fa-chevron-left" style="color: white;"></i>';
  prevBtn.disabled = productsCurrentPage === 1;
  prevBtn.addEventListener('click', () => {
    if (productsCurrentPage > 1) {
      productsCurrentPage--;
      renderProducts(applyAllFilters());
    }
  });

  const nextBtn = document.createElement('button');
  nextBtn.innerHTML = '<i class="fas fa-chevron-right" style="color: white;"></i>';
  nextBtn.disabled = productsCurrentPage === totalPages;
  nextBtn.addEventListener('click', () => {
    if (productsCurrentPage < totalPages) {
      productsCurrentPage++;
      renderProducts(applyAllFilters());
    }
  });
  const pageInfo = document.createElement('span');
  pageInfo.className = 'page-info';
  pageInfo.textContent = `Strona ${productsCurrentPage} z ${totalPages}`;
  paginationContainer.appendChild(prevBtn);
  paginationContainer.appendChild(pageInfo);
  paginationContainer.appendChild(nextBtn);
}
async function deleteProduct(productId) {
  const globalIndex = products.findIndex(p => p.id === productId);
  if (globalIndex === -1) return;
  const p = products[globalIndex];
  logEvent('Naciśnięto przycisk usunięcia dla produktu: ' + p.name);
  showConfirmation(
    'Potwierdzenie usunięcia',
    `Czy na pewno chcesz usunąć produkt "${p.name}"?`,
    async (confirmed) => {
      if (!confirmed) return;
      logEvent('Potwierdzono usunięcie produktu: ' + p.name);
      const productElement = document.querySelector(`.product[data-id="${productId}"]`);
      if (productElement) {
        productElement.style.transform = 'scale(0.9)';
        productElement.style.opacity = '0';
        setTimeout(async () => {
          if (document.body.contains(productElement)) {
            productElement.remove();
          }
          products.splice(globalIndex, 1);
          await saveProducts();
          forceUpdateStatistics();
          const totalPages = Math.ceil(products.length / paginationConfig.productsPerPage);
          if (productsCurrentPage > totalPages && totalPages > 0) {
            productsCurrentPage = totalPages;
          }
          renderProducts(applyAllFilters());
          showNotification('Produkt ' + p.name + ' został usunięty', 'success');
          playNotificationSound('success');
        }, 300);
      } else {
        products.splice(globalIndex, 1);
        await saveProducts();
        forceUpdateStatistics();
        const totalPages = Math.ceil(products.length / paginationConfig.productsPerPage);
        if (productsCurrentPage > totalPages && totalPages > 0) {
          productsCurrentPage = totalPages;
        }
        renderProducts(applyAllFilters());
      }
    }
  );
}
function updateProductInDOM(productId, isEditing) {
  const productIndex = products.findIndex(p => p.id === productId);
  if (productIndex === -1) return;

  const p = products[productIndex];
  const productElement = document.querySelector(`.product[data-id="${productId}"]`);

  if (productElement) {
    const newElement = renderProduct(p, productIndex, isEditing);
    productElement.replaceWith(newElement);
  }
}
function startInline(productId) {
  const globalIndex = products.findIndex(p => p.id === productId);
  if (globalIndex === -1) return;

  logEvent(`Rozpoczęto edycję produktu: ${products[globalIndex].name}`);

  products.forEach(p => p.editing = false);

  products[globalIndex].editing = true;

  updateProductInDOM(productId, true);
  setTimeout(() => {
    const card = document.querySelector(`.product[data-id="${productId}"]`);
    if (card) {
      const categorySelect = card.querySelector('.edit-category');
      if (categorySelect) {
        categorySelect.addEventListener('change', function() {
          updateFormFieldsBasedOnCategory(this.value, card);
        });
      }
    }
  }, 100);
}

function updateFormFieldsBasedOnCategory(category, card) {
  const effectField = card.querySelector('.edit-effect')?.closest('div');
  const conditionField = card.querySelector('.edit-condition')?.closest('div');
  const languageField = card.querySelector('.edit-language')?.closest('div');
  const currentPriceGroup = card.querySelector('.edit-currentPrice')?.closest('div');
  const quantityLabel = card.querySelector('.quantity-label');
  const priceLabel = card.querySelector('.currency-group')?.previousElementSibling;

  if (effectField) effectField.style.display = 'none';
  if (conditionField) conditionField.style.display = 'none';
  if (languageField) languageField.style.display = 'none';
  if (currentPriceGroup) currentPriceGroup.style.display = 'block';

  if (category === 'Singles') {
    if (effectField) effectField.style.display = 'block';
    if (conditionField) conditionField.style.display = 'block';
  }

  if (!['Złoto', 'Akcesoria', 'Inne'].includes(category)) {
    if (languageField) languageField.style.display = 'block';
  }

  if (category === 'Złoto') {
    if (currentPriceGroup) currentPriceGroup.style.display = 'none';
    if (quantityLabel) quantityLabel.textContent = 'Ilość (gram)';
    if (priceLabel) priceLabel.textContent = 'Cena zakupu za 1 gram';
  } else {
    if (quantityLabel) quantityLabel.textContent = 'Ilość';
    if (priceLabel) priceLabel.textContent = 'Cena zakupu jednej sztuki';
  }
}
