async function saveInline(productId) {
  const globalIndex = products.findIndex(p => p.id === productId);
  if (globalIndex === -1) {
    showNotification('Błąd: nie znaleziono produktu do edycji', 'danger');
    return;
  }

  const card = document.querySelector(`.product[data-id="${productId}"]`);
  if (!card) {
    showNotification('Błąd: element edycji nie został znaleziony', 'danger');
    return;
  }

  logEvent(`Zapisywanie zmian produktu: ${products[globalIndex].name}`);

  try {
    const nameInput = card.querySelector('.edit-name');
    const categorySelect = card.querySelector('.edit-category');
    const quantityInput = card.querySelector('.edit-quantity');
    const priceInput = card.querySelector('.edit-price');
    const yearInput = card.querySelector('.edit-year');
    const currencySelect = card.querySelector('.edit-currency');
    const currentPriceInput = card.querySelector('.edit-currentPrice');
    const currentCurrencySelect = card.querySelector('.edit-currentCurrency');
    const effectSelect = card.querySelector('.edit-effect');
    const conditionSelect = card.querySelector('.edit-condition');
    const languageSelect = card.querySelector('.edit-language');

    resetValidationStyles(card);
    const errors = [];

    if (!nameInput || !nameInput.value.trim()) {
      errors.push('Nazwa produktu jest wymagana');
      highlightField(nameInput);
    }

    if (!categorySelect || !categorySelect.value) {
      errors.push('Kategoria jest wymagana');
      highlightField(categorySelect);
    }

    if (!quantityInput || isNaN(parseInt(quantityInput.value)) || parseInt(quantityInput.value) < 1) {
      errors.push('Nieprawidłowa ilość');
      highlightField(quantityInput);
    }

    if (!priceInput || isNaN(parseFloat(priceInput.value)) || parseFloat(priceInput.value) <= 0) {
      errors.push('Nieprawidłowa cena zakupu');
      highlightField(priceInput);
    }

    if (!yearInput || isNaN(parseInt(yearInput.value)) || parseInt(yearInput.value) < 2000 || parseInt(yearInput.value) > new Date().getFullYear() + 1) {
      errors.push('Nieprawidłowy rok zakupu');
      highlightField(yearInput);
    }

    if (!currencySelect || !currencySelect.value) {
      errors.push('Waluta jest wymagana');
      highlightField(currencySelect);
    }

    const category = categorySelect ? categorySelect.value : '';

    if (category === 'Singles') {
      if (!conditionSelect || !conditionSelect.value) {
        errors.push('Stan karty jest wymagany dla kategorii Singles');
        highlightField(conditionSelect);
      }
      if (effectSelect && effectSelect.value && !effects.includes(effectSelect.value)) {
        errors.push('Wybrany efekt nie istnieje na liście dostępnych efektów');
        highlightField(effectSelect);
      }
    }

    if (!['Złoto', 'Akcesoria', 'Inne'].includes(category)) {
      if (!languageSelect || !languageSelect.value) {
        errors.push('Język jest wymagany dla tej kategorii');
        highlightField(languageSelect);
      } else if (!LANGUAGES.includes(languageSelect.value)) {
        errors.push('Wybrany język nie istnieje na liście dostępnych języków');
        highlightField(languageSelect);
      }
    }

    if (category !== 'Złoto') {
      if (currentPriceInput && currentPriceInput.value.trim() !== '') {
        const currentPriceValue = parseFloat(currentPriceInput.value);
        if (isNaN(currentPriceValue) || currentPriceValue < 0) {
          errors.push('Nieprawidłowa aktualna cena');
          highlightField(currentPriceInput);
        }
        if (!currentCurrencySelect || !currentCurrencySelect.value) {
          errors.push('Waluta aktualnej ceny jest wymagana');
          highlightField(currentCurrencySelect);
        }
      }
    }

    if (errors.length > 0) {
      showNotification('Popraw następujące błędy: ' + errors.join(', '), 'danger');
      playNotificationSound('alert');
      return;
    }

    const name = nameInput.value.trim();
    const quantity = parseInt(quantityInput.value);
    const price = parseFloat(priceInput.value);
    const year = parseInt(yearInput.value);
    const currency = currencySelect.value;

    let currentPriceValue = null;
    let currentCurrencyValue = null;

    if (category === 'Złoto') {
      const goldPrice = await fetchGoldPrice();
      if (goldPrice !== null) {
        currentPriceValue = goldPrice;
        currentCurrencyValue = 'PLN';
      } else {
        showNotification('Nie udało się pobrać ceny złota. Używam ostatniej znanej ceny.', 'warning');
        currentPriceValue = exchangeRates.GOLD || price;
        currentCurrencyValue = 'PLN';
      }
    } else {
      if (currentPriceInput && currentPriceInput.value.trim() !== '') {
        currentPriceValue = parseFloat(currentPriceInput.value);
        currentCurrencyValue = currentCurrencySelect ? currentCurrencySelect.value : (products[globalIndex].currentCurrency || currency);
      }
    }

    let effectValue = null;
    if (effectSelect && category === 'Singles') {
      effectValue = effectSelect.value || null;
    }

    let conditionValue = null;
    if (conditionSelect && category === 'Singles') {
      conditionValue = conditionSelect.value;
    }

    let languageValue = null;
    if (languageSelect && !['Złoto', 'Akcesoria', 'Inne'].includes(category)) {
      languageValue = languageSelect.value;
    }

    let newName = name;
    let nameChanged = false;

    const lastPriceUpdate = new Date().toISOString();

    const updatedProduct = {
      ...products[globalIndex],
      name: newName,
      category: category,
      quantity: quantity,
      price: price,
      year: year,
      currency: currency,
      currentPrice: currentPriceValue,
      currentCurrency: currentCurrencyValue,
      effect: effectValue,
      condition: conditionValue,
      language: languageValue,
      editing: false,
      lastPriceUpdate: lastPriceUpdate
    };

    products[globalIndex] = updatedProduct;

    if (currentPriceValue !== null) {
      try {
        await priceHistory.addPricePoint(
          updatedProduct.id,
          currentPriceValue,
          currentCurrencyValue
        );
      } catch (error) {
        logEvent('Błąd zapisu historii cen:', error);
      }
    }

    await saveProducts();

    if (nameChanged) {
      showNotification(`Produkt o takiej nazwie już istnieje. Zapisano jako: ${newName}`, 'warning', 5000);
      playNotificationSound('alert');
    } else {
      showNotification('Produkt został zaktualizowany', 'success');
      playNotificationSound('success');
    }

    renderProducts(applyAllFilters());

  } catch (error) {
    showNotification('Błąd podczas zapisywania: ' + error, 'danger');
    logEvent('Błąd saveInline:', error);
    logEvent(`Błąd podczas zapisu produktu: ${error}`);
  }
}
