async function fetchEffectsFromGitHub() {
    try {
        const response = await fetch('https://raw.githubusercontent.com/BarryV94/Magazyn-Produkt-w/main/effects.json');
        if (!response.ok) {
            throw new Error('Nie udało się pobrać efektów');
        }
        const effectsFromGitHub = await response.json();
        return effectsFromGitHub;
    } catch (error) {
        logEvent('Błąd pobierania efektów:', error);
        return null;
    }
}
async function updateEffectsFromGitHub() {
    const loader = new PokeballLoader('pokeballLoader').start(0);
    loader.update(30);

    try {
        const githubEffects = await fetchEffectsFromGitHub();
        if (githubEffects && githubEffects.length > 0) {
            const existingEffects = new Set(effects);
            const newEffects = githubEffects.filter(effect => !existingEffects.has(effect));

            if (newEffects.length > 0) {
                effects.push(...newEffects);
                saveEffects();
                renderEffectsList();
                loader.complete();
                setTimeout(() => loader.reset(), 500);
                showNotification(`Dodano ${newEffects.length} nowych efektów`, 'success');
                playNotificationSound('success');

                if (document.getElementById('effect')) {
                    fillEffectSelect();
                }
            } else {
                loader.complete();
                setTimeout(() => loader.reset(), 500);
            }
        } else {
            throw new Error('Nie udało się pobrać efektów');
        }
    } catch (error) {
        loader.reset();
        showNotification('Błąd podczas aktualizacji efektów: ' + error.message, 'danger');
    }
}
