    const currentPriceLabel = document.querySelector('#previewCurrentPrice') ? document.querySelector('#previewCurrentPrice').previousElementSibling : null;

    if (!previewProductName) {
      logEvent('Required DOM element previewProductName not found');
      return;
    }

    previewProductName.textContent = product.name;

    const excludedPriceTrendCategories = ['Złoto', 'Inne', 'Akcesoria'];
    const excludedCardmarketCategories = ['Inne', 'Akcesoria'];

    const showPriceTrend = !excludedPriceTrendCategories.includes(product.category);
    const showCardmarket = !excludedCardmarketCategories.includes(product.category);

    const existingCondition = document.querySelector('.preview-card-condition');
    const existingLanguageRow = document.querySelector('.preview-language-row');
    if (existingCondition) existingCondition.remove();
    if (existingLanguageRow) existingLanguageRow.remove();

    if (product.category === 'Singles' && product.condition) {
      const conditionElement = document.createElement('div');
      conditionElement.className = 'preview-card-condition';
      conditionElement.textContent = `Stan karty: ${product.condition}`;
      conditionElement.style.marginTop = '5px';
      conditionElement.style.fontSize = '0.9em';
      conditionElement.style.color = '#666';
      conditionElement.style.display = 'block';
