      if (!goldBuybackRow) {
        const previewDetails = document.querySelector('.preview-details');
        if (previewDetails) {
          goldBuybackRow = document.createElement('div');
          goldBuybackRow.id = 'previewGoldBuyback';
          goldBuybackRow.className = 'preview-detail-row';
