            <div>${formatCurrency(convertedBuyback, targetCurrency, true, 'goldBuyback')}</div>
            <div style="font-size: 0.8em; color: #666; margin-top: 5px;">
              ${result.rateInfo}<br>
              Kurs USD/PLN: ${result.usdRate.toFixed(4)}
              <button onclick="refreshGoldBuybackPrice()" style="margin-top: 5px; padding: 2px 5px; font-size: 0.7em;">
                <i class="fas fa-sync-alt"></i> Odśwież teraz
              </button>
            </div>
          `;
          startGoldBuybackAutoRefresh(product);
        }).catch(error => {
          logEvent('Błąd obliczania ceny odkupu:', error);
          if (goldBuybackValueElement) {
            goldBuybackValueElement.textContent = 'Błąd obliczeń';
          }
        });
