      if (goldBuybackRow) {
        goldBuybackRow.style.display = 'flex';
      }

      if (quantityLabel) quantityLabel.textContent = 'Ilość (gram)';
      if (currentPriceLabel) currentPriceLabel.textContent = 'Aktualna cena za 1 gram';
      const priceLabel = document.querySelector('#previewPrice') ? document.querySelector('#previewPrice').previousElementSibling : null;
