      if (card3dContainer) card3dContainer.style.display = 'none';
      if (standardPreview) standardPreview.style.display = 'block';
      if (productPreviewImg) productPreviewImg.src = product.image || '';
      if (effectRow) effectRow.style.display = 'none';
