        yearRow.parentNode.insertBefore(languageRow, yearRow.nextSibling);
      }
    }

    const currentPrice = product.currentPrice !== null && product.currentPrice !== undefined ? 
      formatCurrency(product.currentPrice, product.currentCurrency || product.currency, true, 'currentPrice') : 'Brak';

    const previewCurrentPriceElement = document.getElementById('previewCurrentPrice');
    if (previewCurrentPriceElement) previewCurrentPriceElement.textContent = currentPrice;

    const previewCurrencyElement = document.getElementById('previewCurrency');
    if (previewCurrencyElement) previewCurrencyElement.textContent = 
      `${product.currency} (${getCurrencySymbol(product.currency)})`;

    const previewCurrentCurrencyElement = document.getElementById('previewCurrentCurrency');
    if (previewCurrentCurrencyElement) previewCurrentCurrencyElement.textContent = 
      `${product.currentCurrency || product.currency} (${getCurrencySymbol(product.currentCurrency || product.currency)})`;

    let priceDiffHtml = '';
    if (product.currentPrice !== null && product.currentPrice !== undefined) {
      const purchasePLN = convertToPLN(product.price, product.currency);
      let diff, diffPerItem, diffPercent, diffClass, diffSign;

      if (product.category === 'Złoto') {
        const totalPurchaseValue = purchasePLN * product.quantity;
        const totalCurrentValue = convertToPLN(product.currentPrice, product.currentCurrency || product.currency) * product.quantity;
        diff = totalCurrentValue - totalPurchaseValue;
        diffPerItem = (totalCurrentValue / product.quantity) - (totalPurchaseValue / product.quantity);
        diffPercent = (diff / totalPurchaseValue) * 100;
        diffClass = diff > 0 ? 'positive' : (diff < 0 ? 'negative' : 'neutral');
        diffSign = diff > 0 ? '+' : '';

        priceDiffHtml = `
          <div class="price-difference ${diffClass}">
            <div>${diffSign}${formatCurrency(Math.abs(diff), 'PLN', true, 'priceDiff')}</div>
            <div>${diffSign}${Math.abs(diffPercent).toFixed(2).replace('.', ',')}%</div>
          </div>
        `;
      } else {
        const currentPLN = convertToPLN(product.currentPrice, product.currentCurrency || product.currency);
        diff = (currentPLN - purchasePLN) * product.quantity;
        diffPerItem = currentPLN - purchasePLN;
        diffPercent = (diffPerItem / purchasePLN) * 100;
        diffClass = diff > 0 ? 'positive' : (diff < 0 ? 'negative' : 'neutral');
        diffSign = diff > 0 ? '+' : '';

        const sameCurrency = product.currency === (product.currentCurrency || product.currency);
        let profitInCurrency = '';

        if (sameCurrency) {
          const profitVal = (product.currentPrice - product.price) * product.quantity;
          profitInCurrency = formatCurrency(Math.abs(profitVal), product.currency, false, 'priceDiff');
        }

        priceDiffHtml = `
          <div class="price-difference ${diffClass}">
            ${sameCurrency ? `
              <div>${diffSign}${profitInCurrency} ${getCurrencySymbol(product.currency)}</div>
              <div>(${diffSign}${formatCurrency(Math.abs(diff), 'PLN', true, 'priceDiff')})</div>
            ` : `
              <div>${diffSign}${formatCurrency(Math.abs(diff), 'PLN', true, 'priceDiff')}</div>
            `}
            <div>${diffSign}${Math.abs(diffPercent).toFixed(2).replace('.', ',')}%</div>
          </div>
        `;
      }
    } else {
      priceDiffHtml = '<div class="price-difference neutral">Brak danych o aktualnej cenie</div>';
    }

    const previewPriceDifferenceElement = document.getElementById('previewPriceDifference');
    if (previewPriceDifferenceElement) {
      previewPriceDifferenceElement.innerHTML = priceDiffHtml;
    }

    let priceTrendRow = document.getElementById('previewPriceTrendRow');
    if (showPriceTrend && priceTrendAllow) {
      if (!priceTrendRow) {
        const previewDetails = document.querySelector('.preview-details');
        if (previewDetails) {
          priceTrendRow = document.createElement('div');
          priceTrendRow.id = 'previewPriceTrendRow';
          priceTrendRow.className = 'detail-item';
          priceTrendRow.innerHTML = `
            <span class="detail-label">Price Trend na Cardmarket:</span>
            <span class="preview-detail-value">
              <div class="loading-trend">Ładowanie...</div>
            </span>
          `;

          const priceDifferenceSection = document.querySelector('.price-difference-section');
          if (priceDifferenceSection) {
            priceDifferenceSection.parentNode.insertBefore(priceTrendRow, priceDifferenceSection);
          } else {
            previewDetails.appendChild(priceTrendRow);
          }
        }
      } else {
        const priceTrendElement = document.getElementById('previewPriceTrend');
        if (priceTrendElement) {
          priceTrendElement.innerHTML = '<div class="loading-trend">Ładowanie...</div>';
        }
      }
      updateProductPreviewPriceTrend(product.name);
    } else if (showPriceTrend && !priceTrendAllow) {
      if (!priceTrendRow) {
        const previewDetails = document.querySelector('.preview-details');
        if (previewDetails) {
          priceTrendRow = document.createElement('div');
          priceTrendRow.id = 'previewPriceTrendRow';
          priceTrendRow.className = 'detail-item';
          priceTrendRow.innerHTML = `
            <span class="detail-label">Price Trend na Cardmarket:</span>
            <span class="preview-detail-value">
              <span class="no-data">Niedostępne</span>
            </span>
          `;

          const priceDifferenceSection = document.querySelector('.price-difference-section');
          if (priceDifferenceSection) {
            priceDifferenceSection.parentNode.insertBefore(priceTrendRow, priceDifferenceSection);
          } else {
            previewDetails.appendChild(priceTrendRow);
          }
        }
      } else {
        const priceTrendElement = document.getElementById('previewPriceTrend');
        if (priceTrendElement) {
          priceTrendElement.innerHTML = '<span class="no-data">Niedostępne</span>';
        }
      }
    } else {
      if (priceTrendRow) {
        priceTrendRow.style.display = 'none';
      }
    }

    currentChartProductId = product.id;
    const historySection = document.querySelector('.price-history-section');
    const chartContainer = document.querySelector('.price-chart-container');

    if (chartContainer) {
      chartContainer.innerHTML = '<div class="price-chart-loading"></div>';

      if (historySection) {
        historySection.style.display = 'block';
      }

      const canvas = document.createElement('canvas');
      canvas.id = 'priceHistoryChart';
      chartContainer.appendChild(canvas);

      setTimeout(async () => {
        try {
          await renderPriceHistoryChart(product.id);
        } catch (error) {
          logEvent('Błąd renderowania wykresu:', error);
          if (chartContainer) {
            chartContainer.innerHTML = '<div class="error-message">Błąd generowania wykresu</div>';
          }
        }
      }, 100);
    }

    const productPreviewModal = document.getElementById('productPreviewModal');
    if (productPreviewModal) {
      productPreviewModal.classList.add('show');

      const handlePreviewKeyDown = function(e) {
        if (e.key === 'Escape') {
          closeProductPreview();
        }
      };
      document.addEventListener('keydown', handlePreviewKeyDown);

      setTimeout(() => {
        const modalContent = productPreviewModal.querySelector('.modal-content');
        const scrollBtn = productPreviewModal.querySelector('.modal-scroll-to-top');
        if (modalContent) modalContent.scrollTop = 0;
        if (scrollBtn) scrollBtn.classList.remove('show');
      }, 100);
    }

  } catch (error) {
    logEvent('Błąd w openProductPreview:', error);
    showNotification('Błąd podczas otwierania podglądu produktu', 'danger');
  }
}
function savePriceTrendCache(productName, trend) {
    if (trend === null) {
        if (priceTrendCache[productName]) {
            delete priceTrendCache[productName];
            localStorage.setItem('priceTrendCache', JSON.stringify(priceTrendCache));
        }
        return;
    }

    priceTrendCache[productName] = {
        trend: trend,
        timestamp: Date.now()
    };
    localStorage.setItem('priceTrendCache', JSON.stringify(priceTrendCache));
}
async function updateProductPreviewPriceTrend(productName, retryCount = 0) {
  if (!priceTrendAllow) {
    const trendElement = document.getElementById('previewPriceTrend');
    if (trendElement) {
      trendElement.innerHTML = '<span class="no-data">Niedostępne</span>';
    }
    return;
  }
  const maxRetries = 2;
  const retryDelay = 10000;

  try {
    const trend = await fetchCardmarketPriceTrend(productName);
    const trendElement = document.getElementById('previewPriceTrend');

    if (!trendElement) return;

    if (trend) {
      trendElement.innerHTML = `<span class="price-trend-value">${trend}</span>`;
    } else {
      if (retryCount < maxRetries) {
        trendElement.innerHTML = `<div class="loading-trend">Ponawianie... (${retryCount + 1}/${maxRetries})</div>`;
        setTimeout(() => {
          updateProductPreviewPriceTrend(productName, retryCount + 1);
        }, retryDelay);
      } else {
        trendElement.innerHTML = '<span class="no-data">Brak danych po wielu próbach</span>';
      }
    }
  } catch (error) {
    const trendElement = document.getElementById('previewPriceTrend');

    if (trendElement) {
      if (retryCount < maxRetries) {
        trendElement.innerHTML = `<div class="loading-trend">Błąd, ponawianie... (${retryCount + 1}/${maxRetries})</div>`;
        setTimeout(() => {
          updateProductPreviewPriceTrend(productName, retryCount + 1);
        }, retryDelay);
      } else {
        trendElement.innerHTML = '<span class="no-data">Błąd pobierania</span>';
      }
    }
  }
}

function isPriceTrendCacheValid(productName) {
    const cached = priceTrendCache[productName];
    if (!cached) return false;

    const now = Date.now();
    return (now - cached.timestamp) < PRICE_TREND_CACHE_DURATION;
}
async function fetchCardmarketPriceTrend(productName) {
	if (!priceTrendAllow) {
	return null;
	}
    if (isPriceTrendCacheValid(productName)) {
        return priceTrendCache[productName].trend;
    }

    try {        
        const searchUrl = await searchCardmarketForProduct(productName);
        if (!searchUrl) {
            logEvent('Nie udało się utworzyć URL wyszukiwania');
            savePriceTrendCache(productName, null);
            return null;
        }

        const priceTrend = await extractPriceTrendFromPage(searchUrl);

        if (priceTrend) {
            logEvent('Znaleziono Price Trend:', priceTrend);
            savePriceTrendCache(productName, priceTrend);
        } else {
            savePriceTrendCache(productName, null);
        }

        return priceTrend;

    } catch (error) {
        savePriceTrendCache(productName, null);
        return null;
    }
}
async function searchCardmarketForProduct(productName) {
  try {
    let sanitized = productName.replace(/[^A-Za-z0-9ĄąĆćĘęŁłŃńÓóŚśŹźŻż\s]/g, ' ');
    sanitized = sanitized.replace(/\s+/g, ' ').trim();

    sanitized = sanitized.replace(/\s*(Holo|Reverse|Foil|Rare|Common|Uncommon|Secret|Ultra|GX|EX|V|VMAX|VSTAR)$/gi, '').trim();

    const exactSearchString = `[${sanitized}]`;
    const formattedName = encodeURIComponent(exactSearchString)
      .replace(/%20/g, '+')
      .replace(/'/g, '%27');

    const searchUrl = `https://www.cardmarket.com/en/Pokemon/Products/Search?searchString=${formattedName}&mode=gallery`;

    return searchUrl;
  } catch (error) {
    return null;
  }
}
async function extractPriceTrendFromPage(searchUrl) {
  try {
    const proxies = [
      `https://api.allorigins.win/get?url=${encodeURIComponent(searchUrl)}`,
      `https://corsproxy.io/?${encodeURIComponent(searchUrl)}`,
      `https://api.codetabs.com/v1/proxy?quest=${encodeURIComponent(searchUrl)}`
    ];

    for (const proxyUrl of proxies) {
      try {
        const response = await fetch(proxyUrl, {
          method: 'GET',
          headers: {
            'Accept': 'text/html,application/xhtml+xml,application/xml;q=0.9,image/webp,*/*;q=0.8',
            'Accept-Language': 'en-US,en;q=0.5',
            'Cache-Control': 'no-cache',
            'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/91.0.4472.124 Safari/537.36'
          }
        });

        if (!response.ok) {
          continue;
        }

        let htmlContent;

        if (proxyUrl.includes('api.allorigins.win')) {
          const data = await response.json();
          htmlContent = data.contents;
        } else {
          htmlContent = await response.text();
        }

        if (!htmlContent) {
          logEvent('Brak zawartości HTML');
          continue;
        }

        const parser = new DOMParser();
        const doc = parser.parseFromString(htmlContent, 'text/html');


        const priceTrendElements = Array.from(doc.querySelectorAll('*')).filter(el => 
          el.textContent && el.textContent.trim() === 'Price Trend'
        );

        for (const element of priceTrendElements) {
          let nextElement = element.nextElementSibling;
          while (nextElement) {
            const text = nextElement.textContent.trim();
            if (text && (text.includes('€') || text.includes('$') || text.includes('£') || /\d+[,.]\d+/.test(text))) {
              return text;
            }
            nextElement = nextElement.nextElementSibling;
          }
        }

        const priceSelectors = [
          '.price-trend',
          '.trend-price',
          '[class*="price"]',
          '[class*="trend"]',
          '.col-6.col-xl-7 span',
          '.row .col-6:last-child span'
        ];

        for (const selector of priceSelectors) {
          const elements = doc.querySelectorAll(selector);
          for (const element of elements) {
            const text = element.textContent.trim();
            if (text && (text.includes('€') || text.includes('$') || text.includes('£')) && /\d+[,.]\d+/.test(text)) {
              logEvent('Znaleziono cenę przez selektor:', selector, text);
              return text;
            }
          }
        }

        const dtElements = doc.querySelectorAll('dt');
        for (const dt of dtElements) {
          if (dt.textContent.trim() === 'Price Trend') {
            const dd = dt.nextElementSibling;
            if (dd && dd.tagName === 'DD') {
              const priceElement = dd.querySelector('span') || dd;
              const priceText = priceElement.textContent.trim();
              if (priceText) {
                return priceText;
              }
            }
          }
        }

        const dataElements = doc.querySelectorAll('[data-price], [data-trend]');
        for (const element of dataElements) {
          const price = element.getAttribute('data-price') || element.getAttribute('data-trend');
          if (price) {
            return price + ' €';
          }
        }

      } catch (proxyError) {
      }
    }

    return null;

  } catch (error) {
    return null;
  }
}
async function fetchPriceTrendAlternative(productName) {
  try {
    const apiKey = 'twój_api_key';
    const apiUrl = `https://api.scraperapi.com/?api_key=${apiKey}&url=https://www.cardmarket.com/en/Pokemon/Products/Search&query=${encodeURIComponent(productName)}`;
    const response = await fetch(apiUrl);
