async function deletePricePoint(pointId) {
    if (pointId == null) return;
    const loader = new PokeballLoader('pokeballLoader').start(0);
    try {
        const db = await priceHistory.dbPromise;
        const tx = db.transaction('price_history', 'readwrite');
        const store = tx.objectStore('price_history');
        let actualId = pointId;
        if (typeof pointId === 'string' && pointId.startsWith('temp-')) {
            actualId = pointId.slice('temp-'.length);
        }
        if (typeof actualId === 'string' && !isNaN(Number(actualId))) {
            actualId = Number(actualId);
        }
        await store.delete(actualId);
        await tx.done;
        const overlay = document.querySelector('.point-details-overlay');
        if (overlay) document.body.removeChild(overlay);
        if (window.pricePoints && Array.isArray(window.pricePoints)) {
            window.pricePoints = window.pricePoints.filter(p => p.id !== actualId);
        }
        const prod = products.find(p => p.id === currentChartProductId);
        if (prod && Array.isArray(prod.priceHistory)) {
            prod.priceHistory = prod.priceHistory.filter(p => p.id !== actualId);
        }
        await renderPriceHistoryChart(currentChartProductId);
        showNotification('Punkt został usunięty', 'success');
		playNotificationSound('success');
    } catch (error) {
        showNotification('Błąd usuwania punktu', 'danger');
    } finally {
        loader.reset();
    }
}
