function init3DCardEffect() {
  const cardContainer = document.getElementById('card3dContainer');
  if (!cardContainer) return;
  const card = cardContainer.querySelector('.card');
  let isInteracting = false;
  function updateCardPosition(localX, localY) {
    const w = cardContainer.clientWidth;
    const h = cardContainer.clientHeight;
    const centerX = w / 2;
    const centerY = h / 2;
    const rotateY = -(localX - centerX) / 10;
    const rotateX = -(centerY - localY) / 10;
    card.style.transform = `rotateX(${rotateX}deg) rotateY(${rotateY}deg)`;
  }
  cardContainer.addEventListener('mousemove', e => {
    isInteracting = true;
    const rect = cardContainer.getBoundingClientRect();
    updateCardPosition(e.clientX - rect.left, e.clientY - rect.top);
  });
  cardContainer.addEventListener('mouseleave', () => {
    isInteracting = false;
    card.style.transform = 'none';
  });
  cardContainer.addEventListener('touchstart', e => {
    e.preventDefault();
    isInteracting = true;
    const t = e.touches[0];
    const rect = cardContainer.getBoundingClientRect();
    updateCardPosition(t.clientX - rect.left, t.clientY - rect.top);
  }, { passive: false });
  cardContainer.addEventListener('touchmove', e => {
    if (!isInteracting) return;
    e.preventDefault();
    const t = e.touches[0];
    const rect = cardContainer.getBoundingClientRect();
    updateCardPosition(t.clientX - rect.left, t.clientY - rect.top);
  }, { passive: false });
  cardContainer.addEventListener('touchend', () => {
    isInteracting = false;
    card.style.transform = 'none';
  });
}
