        zip.file('price_history.json', JSON.stringify(allPriceHistory, null, 2));

        loader.update(40);

        const priceTrendCacheData = JSON.stringify({
            priceTrendCache: priceTrendCache,
            cacheExportDate: new Date().toISOString(),
            cacheEntries: Object.keys(priceTrendCache).length
        }, null, 2);
        zip.file('price_trend_cache.json', priceTrendCacheData);

        const imgFolder = zip.folder('images');

        const productImgFolder = imgFolder.folder('products');
        for (const product of products) {
            if (product.image) {
                const isFromGitHub = galleryImages.some(img => 
                    img.data === product.image && img.source === 'github'
                );

                if (!isFromGitHub) {
                    try {
                        const base64Data = product.image.split(',')[1];
                        const fileName = `${product.id}.png`;
                        productImgFolder.file(fileName, base64Data, {base64: true});
                    } catch (error) {
                    }
                }
            }
        }

        loader.update(60);

        const galleryFolder = imgFolder.folder('gallery');
        const userGalleryImages = galleryImages.filter(img => 
            !img.source || img.source !== 'github'
        );
        const uniqueGalleryImages = removeDuplicatesWithGitHubPriority(userGalleryImages);
        for (const img of uniqueGalleryImages) {
            try {
                const base64Data = img.data.split(',')[1];
                const fileName = `${img.name}.png`;
                galleryFolder.file(fileName, base64Data, {base64: true});
                logEvent(`Eksportowano obraz galerii: "${img.name}"`);
            } catch (error) {
                logEvent('Błąd eksportu obrazu galerii:', img.name, error);
            }
        }

        loader.update(80);

        const backgroundFolder = imgFolder.folder('backgrounds');
        const userBackgroundImages = backgroundImages.filter(bg => 
            !bg.source || bg.source !== 'github'
        );
        const uniqueBackgroundImages = removeDuplicatesWithGitHubPriority(userBackgroundImages);
        for (const bg of uniqueBackgroundImages) {
            try {
                const base64Data = bg.data.split(',')[1];
                const fileName = `${bg.name}.png`;
                backgroundFolder.file(fileName, base64Data, {base64: true});
                logEvent(`Eksportowano tło: "${bg.name}"`);
            } catch (error) {
                logEvent('Błąd eksportu tła:', bg.name, error);
            }
        }

        const selectionState = {
            selectedProducts: selectedProducts,
            selectedGalleryImages: selectedGalleryImages,
            selectedBackgroundImages: selectedBackgroundImages,
            selectedBackgroundId: selectedBackgroundId,
            selectionMode: selectionMode,
            gallerySelectionMode: gallerySelectionMode,
            backgroundSelectionMode: backgroundSelectionMode
        };
        zip.file('selection_state.json', JSON.stringify(selectionState, null, 2));

        loader.update(90);

        const content = await zip.generateAsync({ type: 'blob' });
        const fileName = `collection_export_${new Date().toISOString().replace(/:/g, 'H').replace('T', '_').split('.')[0]}.zip`;

        if (typeof Android !== 'undefined') {
            const reader = new FileReader();
            reader.onload = function() {
                const base64Data = reader.result.split(',')[1];
                Android.saveExportFile(fileName, base64Data);
            };
            reader.readAsDataURL(content);
        } else {
            const url = URL.createObjectURL(content);
            const a = document.createElement('a');
            a.href = url;
            a.download = fileName;
            document.body.appendChild(a);
            a.click();
            document.body.removeChild(a);
            URL.revokeObjectURL(url);
        }

        loader.complete();
        setTimeout(() => {
            loader.reset();
            showNotification('Eksport zakończony pomyślnie', 'success');
            playNotificationSound('success');
        }, 500);

    } catch (error) {
        loader.reset();
        showNotification('Błąd eksportu danych: ' + error.message, 'danger');
    }
}
async function getAllPriceHistory() {
    const db = await priceHistory.dbPromise;
    return new Promise((resolve, reject) => {
        const tx = db.transaction('price_history', 'readonly');
        const store = tx.objectStore('price_history');
        const request = store.getAll();
        request.onsuccess = () => {
            const data = request.result || [];
            resolve(data);
        };
        request.onerror = () => {
            reject('Błąd pobierania historii cen');
        };
    });
}
