async function importPriceHistory(historyData) {
    const db = await priceHistory.dbPromise;
    return new Promise((resolve, reject) => {
        const tx = db.transaction('price_history', 'readwrite');
        const store = tx.objectStore('price_history');
        const clearRequest = store.clear();
        clearRequest.onsuccess = () => {
            const requests = historyData.map(entry => store.add(entry));
            Promise.all(requests)
                .then(() => resolve())
                .catch(error => reject(error));
        };
        clearRequest.onerror = (e) => reject(e.target.error);
    });
}
